import org.apache.commons.jexl3.JexlException;
import org.apache.commons.jexl3.JexlExpression;
import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;
import static org.assertj.core.api.Assertions.assertThatThrownBy;

public class JexlPermissionsTest {

    @Test
    public void testIntrospectionComponentAccessControl() {
        String introspectionComponent = "org.apache.commons.jexl3.introspection.JexlUberspect";
        String createExpr = "new('" + introspectionComponent + "', null, null)";

        // 1. Configure with a restrictive policy that blocks the introspection utility
        JexlPermissions restrictivePermissions = JexlPermissions.block(introspectionComponent);
        JexlEngine restrictiveEngine = new JexlBuilder().permissions(restrictivePermissions).create();
        JexlExpression restrictiveScript = restrictiveEngine.createExpression(createExpr);

        // 2. Attempting to instantiate should fail with an authorization error
        assertThatThrownBy(() -> restrictiveScript.evaluate(null))
                .isInstanceOf(JexlException.class)
                .hasMessageContaining("denied");

        // 3. Establish a more permissive policy that explicitly grants access
        JexlPermissions permissivePermissions = JexlPermissions.allow(introspectionComponent);
        JexlEngine permissiveEngine = new JexlBuilder().permissions(permissivePermissions).create();
        JexlExpression permissiveScript = permissiveEngine.createExpression(createExpr);

        // 4. The same instantiation should now succeed
        Object result = permissiveScript.evaluate(null);
        assertThat(result).isInstanceOf(JexlUberspect.class);
    }
}

