package org.apache.commons.jexl3;

import org.apache.commons.jexl3.introspection.JexlPermissions;
import org.apache.commons.jexl3.internal.Debugger;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

/**
 * Defines a test case for validating permission enforcement within the JEXL engine's newInstance capability.
 * This test ensures that the engine correctly restricts and allows class instantiation based on the configured
 * JexlPermissions.
 */
public class instantiation {

    /**
     * This test validates that the JEXL engine enforces permissions when creating new class instances.
     * It first confirms that attempting to instantiate a class from a restricted package (in this case,
     * an internal JEXL class) is properly blocked, resulting in a JexlException.Method.
     * It then verifies that after configuring the engine with explicit permissions to allow that specific class,
     * the instantiation succeeds and returns a non-null object of the expected type.
     *
     * Note: The test description specified using 'org.apache.commons.jexl3.introspection.JexlUberspect' as
     * the target class. However, that class does not have a public no-argument constructor, which would cause
     * the second part of this test to fail for reasons unrelated to permissions. To fulfill the test's objective
     * of validating the permission system, 'org.apache.commons.jexl3.internal.Debugger' is used instead, as it resides
     * in a restricted package and has a public default constructor, allowing both the restricted and permitted
     * scenarios to be tested correctly.
     */
    @Test
    public void testNewInstancePermissionEnforcement() {
        // A class within a package that should be blocked by restricted permissions.
        final String internalClassName = "org.apache.commons.jexl3.internal.Debugger";

        // 1. Construct an engine with restricted permissions and disabled silent mode.
        final JexlEngine jexlEngineRestricted = new JexlBuilder()
                .silent(false)
                .permissions(JexlPermissions.RESTRICTED)
                .create();

        // 2. Verify that instantiating the internal class throws a method invocation exception.
        Assertions.assertThrows(JexlException.Method.class, () -> {
            jexlEngineRestricted.newInstance(internalClassName);
        }, "Instantiation should be denied by restricted permissions.");

        // 3. Construct a second engine with permissions explicitly allowing the internal class.
        final JexlPermissions allowedPermissions = JexlPermissions.allow(internalClassName);
        final JexlEngine jexlEngineAllowed = new JexlBuilder()
                .silent(false)
                .permissions(allowedPermissions)
                .create();

        // 4. Invoke newInstance with null constructor arguments and assert a non-null result.
        final Object instance = jexlEngineAllowed.newInstance(internalClassName, (Object[]) null);

        Assertions.assertNotNull(instance, "Instantiation should succeed when explicitly permitted.");
        Assertions.assertTrue(instance instanceof Debugger, "The created instance should be of the correct type.");
    }
}

