package org.apache.commons.jexl3;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * Test case to validate lexical scoping rules with captured variables.
 *
 * This test verifies that a local variable declaration within a function correctly
 * shadows a variable from an outer scope, even when an inner function (closure)
 * modifies the captured outer variable.
 */
public class LexicalScopingTest {

    /**
     * Tests that a local variable shadows a captured outer variable which is modified by a nested function.
     *
     * @throws Exception if any error occurs during script creation or execution.
     */
    @Test
    public void testLexicalShadowingWithCapturedVariableModification() throws Exception {
        // The script sets up a scenario to test lexical scoping:
        // 1. `a` is declared at the top level.
        // 2. `f` is a function (lambda) that captures the outer `a` and increments it.
        // 3. `g` is a function that declares its own local `a` (shadowing the outer one).
        // 4. Inside `g`, `f` is called. This should modify the outer `a`, not the local one in `g`.
        // 5. `g` returns the value of its local `a`.
        final String scriptSource =
                "var a = 10;" +
                "var f = () -> { a = a + 1; };" +
                "var g = () -> { var a = 42; f(); return a; };" +
                "g()";

        // Configure the JexlEngine to enable lexical scoping and allow side effects
        // on captured variables (reference capture).
        JexlFeatures features = new JexlFeatures().lexical(true);
        JexlEngine jexl = new JexlBuilder().features(features).create();
        JexlScript script = jexl.createScript(scriptSource);

        // Execute the script without any external context.
        final Object result = script.execute(null);

        // The expected result is 42, the initial value of the local `a` inside `g`.
        // This confirms that the local declaration shadowed the outer `a`, and the
        // modification performed by `f` on the captured variable did not affect
        // the local scope of `g`.
        assertEquals(42, result, "The local variable's value should not be affected by the inner function's modification of the captured variable.");
    }
}

