import static org.assertj.core.api.Assertions.assertThat;
import static org.junit.jupiter.api.Assertions.assertTrue;

/**
 * Tests for reference capture in lambda functions.
 */
public class LexicalCaptureTest {

    private static final JexlEngine JEXL = new JexlBuilder().create();

    private void validateScript(JexlScript script) {
        Debugger debugger = new Debugger();
        debugger.debug(script);
        assertTrue(debugger.validate(), "Debugger validation failed for script");
    }

    @Test
    public void testReferenceCaptureInLambda() {
        // 1. Configure features for reference capture (lexical scoping)
        JexlFeatures features = new JexlFeatures().lexical(true);

        // 2. Create a JexlEngine with these features
        JexlEngine jexl = new JexlBuilder().features(features).create();

        // 3. Prepare the script with nested functions and variable shadowing
        String scriptSource = "var x = 42;
"
                            + "var y = 169;
"
                            + "var foo = () -> { x = -x; };
"
                            + "var bar = () -> {
"
                            + "  var x = -169;
"
                            + "  foo();
"
                            + "  return x;
"
                            + "};
"
                            + "bar();";

        // 4. Create the JexlScript instance
        JexlScript script = jexl.createScript(scriptSource);

        // 5. Validate that the script object is not null
        assertThat(script).isNotNull();

        // 6. Execute the script and get the result
        Object result = script.execute(null);

        // 7. Assert that the result confirms the scoping rules
        assertThat(result).isEqualTo(-169);

        // 8. Perform debugger validation on a script from the class-level engine
        JexlScript scriptForValidation = JEXL.createScript(scriptSource);
        validateScript(scriptForValidation);
    }
}

