package org.apache.commons.jexl3.internal;

import org.apache.commons.jexl3.JexlCache;
import org.junit.Assert;
import org.junit.Test;

import java.util.Map;

/**
 * Tests for MetaCache.
 *
 * @since 3.2
 */
public class MetaCacheTest {

    @Test
    public void testWeakReferenceCacheTracking() throws Exception {
        final JexlCache.Factory factory = SoftCache::new;
        final MetaCache metaCache = new MetaCache(factory);

        // Create and populate the first cache
        Map<Integer, String> cache1 = metaCache.createCache(3);
        cache1.put(1, "one");
        cache1.put(2, "two");
        cache1.put(3, "three");

        Assert.assertEquals("Cache 1 should have 3 entries", 3, cache1.size());
        Assert.assertEquals("one", cache1.get(1));
        Assert.assertEquals("two", cache1.get(2));
        Assert.assertEquals("three", cache1.get(3));

        // Add a fourth entry to trigger eviction
        cache1.put(4, "four");
        Assert.assertEquals("Cache 1 should maintain its size limit of 3", 3, cache1.size());
        Assert.assertNull("First entry should have been evicted", cache1.get(1));
        Assert.assertEquals("two", cache1.get(2));
        Assert.assertEquals("three", cache1.get(3));
        Assert.assertEquals("four", cache1.get(4));

        // Create and populate the second cache
        Map<String, String> cache2 = metaCache.createCache(2);
        cache2.put("a", "A");
        cache2.put("b", "B");

        Assert.assertEquals("Cache 2 should have 2 entries", 2, cache2.size());
        Assert.assertEquals("A", cache2.get("a"));
        Assert.assertEquals("B", cache2.get("b"));

        // Add a third entry to trigger eviction
        cache2.put("c", "C");
        Assert.assertEquals("Cache 2 should maintain its size limit of 2", 2, cache2.size());
        Assert.assertNull("First entry of cache 2 should be evicted", cache2.get("a"));
        Assert.assertEquals("B", cache2.get("b"));
        Assert.assertEquals("C", cache2.get("c"));

        // Verify MetaCache is tracking both caches
        Assert.assertEquals("MetaCache should be tracking 2 caches", 2, metaCache.size());

        // Nullify strong references to allow garbage collection
        cache1 = null;
        cache2 = null;
        Assert.assertNull(cache1);
        Assert.assertNull(cache2);


        // Poll for a short period to allow the GC to reclaim the weakly-referenced caches
        int attempts = 0;
        while (metaCache.size() > 0 && attempts < 5) {
            System.gc();
            Thread.sleep(100);
            attempts++;
        }

        // Verify that the caches have been garbage collected and removed from the MetaCache
        Assert.assertEquals("MetaCache should be empty after GC", 0, metaCache.size());
    }
}

