import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;
import static org.junit.jupiter.api.Assertions.assertNull;

/**
 * Tests for JexlCache functionality
 */
public class JexlCacheTest {

    @Test
    public void testCacheEvictionAndMemoryReclamation() {
        JexlBuilder builder = new JexlBuilder();

        // Create and populate the first cache with a capacity of 3
        JexlCache<String, Integer> cache1 = builder.createCache(3);
        cache1.put("key1", 1);
        cache1.put("key2", 2);
        cache1.put("key3", 3);

        // Verify the initial state of the first cache
        assertEquals(3, cache1.size());
        assertEquals(1, cache1.get("key1"));
        assertEquals(2, cache1.get("key2"));
        assertEquals(3, cache1.get("key3"));

        // Add a fourth entry, which should trigger the eviction of the oldest entry ("key1")
        cache1.put("key4", 4);

        // Verify that the oldest entry was evicted and the cache size is maintained
        assertEquals(3, cache1.size());
        assertNull(cache1.get("key1"), "The oldest entry should have been evicted.");
        assertNotNull(cache1.get("key2"), "The second entry should still be in the cache.");
        assertNotNull(cache1.get("key3"), "The third entry should still be in the cache.");
        assertNotNull(cache1.get("key4"), "The newest entry should be in the cache.");

        // Create and populate the second cache with a capacity of 2
        JexlCache<String, Integer> cache2 = builder.createCache(2);
        cache2.put("k1", 10);
        cache2.put("k2", 20);

        // Verify the initial state of the second cache
        assertEquals(2, cache2.size());
        assertEquals(10, cache2.get("k1"));
        assertEquals(20, cache2.get("k2"));

        // Add a third entry to the second cache, which should evict the oldest entry ("k1")
        cache2.put("k3", 30);

        // Verify that the oldest entry was evicted and the cache size is maintained
        assertEquals(2, cache2.size());
        assertNull(cache2.get("k1"), "The oldest entry in the second cache should have been evicted.");
        assertNotNull(cache2.get("k2"), "The second entry in the second cache should still be present.");
        assertNotNull(cache2.get("k3"), "The newest entry in the second cache should be present.");

        // Release references to the caches to allow for garbage collection
        cache1 = null;
        cache2 = null;

        // Suggest to the JVM that it should run the garbage collector.
        // This is part of simulating the memory reclamation process.
        System.gc();

        // In a real-world scenario with a cache management system that tracks active caches,
        // one would now verify that the system no longer tracks the released caches.
        // However, Jexl's cache mechanism does not provide a public API to perform this verification.
    }
}

