package org.apache.commons.jexl3.internal;

import org.apache.commons.jexl3.JexlCache;
import org.junit.jupiter.api.Test;

import java.util.LinkedHashMap;
import java.util.Map;
import java.util.function.IntFunction;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNull;

public class MetaCacheTest {

    private static class ConcurrentCache<K, V> extends LinkedHashMap<K, V> implements JexlCache<K, V> {
        private final int capacity;

        public ConcurrentCache(int capacity) {
            super(capacity, 0.75f, true);
            this.capacity = capacity;
        }

        @Override
        protected boolean removeEldestEntry(Map.Entry<K, V> eldest) {
            return size() > capacity;
        }
    }

    @Test
    public void testMetaCache() throws Exception {
        MetaCache<Integer, String> mc = new MetaCache<>(ConcurrentCache::new);
        JexlCache<Integer, String> cache1 = mc.createCache(3);
        cache1.put(1, "one");
        cache1.put(2, "two");
        cache1.put(3, "three");
        assertEquals(3, cache1.size());
        assertEquals("one", cache1.get(1));
        assertEquals("two", cache1.get(2));
        assertEquals("three", cache1.get(3));
        cache1.put(4, "four");
        assertEquals(3, cache1.size());
        assertNull(cache1.get(1));
        assertEquals("two", cache1.get(2));
        assertEquals("three", cache1.get(3));
        assertEquals("four", cache1.get(4));

        JexlCache<String, String> cache2 = (JexlCache<String, String>) (JexlCache<?, ?>) mc.createCache(2);
        cache2.put("a", "A");
        cache2.put("b", "B");
        assertEquals(2, cache2.size());
        assertEquals("A", cache2.get("a"));
        assertEquals("B", cache2.get("b"));
        cache2.put("c", "C");
        assertEquals(2, cache2.size());
        assertNull(cache2.get("a"));
        assertEquals("B", cache2.get("b"));
        assertEquals("C", cache2.get("c"));

        assertEquals(2, mc.size());
        cache1 = null;
        assertNull(cache1);
        cache2 = null;
        assertNull(cache2);
        System.gc();
        for (int i = 0; i < 5 && mc.size() != 0; i++) {
            try {
                Thread.sleep(100);
            } catch (InterruptedException xint) {
                // ignore
            }
        }
        assertEquals(0, mc.size(), "metacache should have no more cache references");
    }
}

package org.apache.commons.jexl3;

import org.apache.commons.jexl3.JexlEngine.AnnotationProcessor;
import org.apache.commons.jexl3.parser.ASTBlock;
import org.apache.commons.jexl3.parser.JexlNode;
import org.junit.jupiter.api.Test;

import java.util.Collection;
import java.util.Collections;
import java.util.concurrent.Callable;

import static org.assertj.core.api.Assertions.assertThat;

/**
 * Tests for various block structures and their return value semantics.
 * @since 3.4
 */
public class BlockTest1 {

    /**
     * An annotation processor that captures whether it was invoked on a synchronized block.
     */
    public static class CaptureAnnotation implements AnnotationProcessor {
        private int invocations = 0;
        private boolean sync = false;

        @Override
        public Callable<Object> process(final JexlEngine jexl, final JexlNode node, final Object... args) {
            invocations++;
            // The annotation is on a statement, which may contain a block.
            JexlNode blockNode = node;
            if (node.jjtGetNumChildren() > 0 && node.jjtGetChild(0) instanceof ASTBlock) {
                blockNode = node.jjtGetChild(0);
            }
            if (blockNode instanceof ASTBlock) {
                sync = ((ASTBlock) blockNode).isSynchronized();
            }
            // Return a callable that will execute the annotated node.
            // It will run within the interpreter's context, allowing execution to proceed.
            return () -> {
                final JexlContext jc = JexlEngine.getThreadContext();
                return jexl.createInterpreter(jc).interpret(node);
            };
        }

        public void reset() {
            invocations = 0;
            sync = false;
        }

        public int getInvocations() {
            return invocations;
        }

        public boolean isSync() {
            return sync;
        }
    }

    @Test
    public void testBlocks() throws Exception {
        final CaptureAnnotation capture = new CaptureAnnotation();
        final JexlFeatures features = new JexlFeatures().supportsAnnotation(true);
        final JexlEngine jexl = new JexlBuilder().features(features).annotation("a", capture).create();
        final JexlContext jc = new MapContext();

        Object result;

        // 1. Annotated synchronized block with statement terminator returns scalar
        capture.reset();
        result = jexl.createScript("@a synchronized { 2; }").execute(jc);
        assertThat(result).isEqualTo(2);
        assertThat(capture.getInvocations()).isEqualTo(1);
        assertThat(capture.isSync()).isTrue();

        // 2. Annotated synchronized block without statement terminator returns collection
        capture.reset();
        result = jexl.createScript("@a synchronized { 2 }").execute(jc);
        assertThat(result).isInstanceOf(Collection.class);
        assertThat((Collection<?>) result).containsExactly(2);
        assertThat(capture.getInvocations()).isEqualTo(1);
        assertThat(capture.isSync()).isTrue();

        // 3. Unannotated block without terminator returns collection
        result = jexl.createScript("{ 2 }").execute(jc);
        assertThat(result).isInstanceOf(Collection.class);
        assertThat((Collection<?>) result).containsExactly(2);

        // 4. Unannotated block with terminator returns scalar
        result = jexl.createScript("{ 2; }").execute(jc);
        assertThat(result).isEqualTo(2);

        // 5. Map literal access by key
        result = jexl.createScript("{'x': 42}.x").execute(jc);
        assertThat(result).isEqualTo(42);

        // 6. Simple assignment block returns value
        result = jexl.createScript("{var x = 42; x;}").execute(jc);
        assertThat(result).isEqualTo(42);

        // 7. Synchronized block with parameters
        capture.reset();
        result = jexl.createScript("@a(123) synchronized { 4; }").execute(jc);
        assertThat(result).isEqualTo(4);
        assertThat(capture.getInvocations()).isEqualTo(1);

        // 8. Unannotated assignment block without terminator returns collection
        result = jexl.createScript("{var x = 42; x}").execute(jc);
        assertThat(result).isInstanceOf(Collection.class);
        assertThat((Collection<?>) result).containsExactly(42);

        // 9. Parameterized synchronized assignment block without terminator returns collection
        capture.reset();
        result = jexl.createScript("@a(1) synchronized {var x = 42; x}").execute(jc);
        assertThat(result).isInstanceOf(Collection.class);
        assertThat((Collection<?>) result).containsExactly(42);
        assertThat(capture.getInvocations()).isEqualTo(1);
        assertThat(capture.isSync()).isTrue();

        // 10. Multi-statement assignment block with mixed terminators returns final value
        result = jexl.createScript("{ var x = 10; var y = 20; x + y }").execute(jc);
        assertThat(result).isInstanceOf(Collection.class);
        assertThat((Collection<?>) result).containsExactly(30);

        // 11. Expression-only block with terminator returns scalar
        result = jexl.createScript("{ 1 + 1; }").execute(jc);
        assertThat(result).isEqualTo(2);

        // 12. Expression-only block without terminator returns collection
        result = jexl.createScript("{ 1 + 1 }").execute(jc);
        assertThat(result).isInstanceOf(Collection.class);
        assertThat((Collection<?>) result).containsExactly(2);

        // 13. Parameterized synchronized expression block behaves consistently (returns scalar due to terminator)
        capture.reset();
        result = jexl.createScript("@a(true) synchronized { 3 + 4; }").execute(jc);
        assertThat(result).isEqualTo(7);
        assertThat(capture.getInvocations()).isEqualTo(1);
        assertThat(capture.isSync()).isTrue();
    }
}

package org.apache.commons.jexl3;

import java.util.ArrayList;
import java.util.Collections;
import java.util.HashMap;
import java.util.HashSet;
import java.util.List;
import java.util.Map;
import java.util.Set;
import java.util.concurrent.atomic.AtomicInteger;
import org.apache.commons.jexl3.internal.Script;
import org.junit.Assert;
import org.junit.Test;

public class BlockStatementTest {

    /**
     * A handler for annotations that counts calls and names.
     * The script annotation '@synchronized' is problematic as 'synchronized' is a Java keyword.
     * We use a handler method named 'synchronizedd' and map the 'synchronized' annotation to it.
     */
    public static class AnnotationContext {
        private final AtomicInteger count = new AtomicInteger(0);
        private final Set<String> names = new HashSet<>();

        public Object synchronizedd(final Object... o) {
            count.incrementAndGet();
            names.add("synchronized");
            // in case an argument is passed, return it
            return o != null && o.length > 0 ? o[0] : this;
        }

        public int getCount() {
            return count.get();
        }

        public Set<String> getNames() {
            return names;
        }
    }

    /**
     * Helper to validates the internal consistency of a parsed script by
     * reconstructing it from its syntax tree and comparing it against the original.
     * @param jexl The JEXL engine
     * @param script The script to check
     */
    private void checkParse(final JexlEngine jexl, final String script) {
        final JexlScript jexlScript = jexl.createScript(script);
        Assert.assertNotNull(jexlScript);
        final String reconstructed = ((Script) jexlScript).getParsedText();
        Assert.assertEquals(script, reconstructed);
    }

    @Test
    public void testBlockConstructsAndAnnotations() throws Exception {
        final AnnotationContext annotationContext = new AnnotationContext();
        final Map<String, Object> annotationHandlers = new HashMap<>();
        // Map the 'synchronized' annotation to the 'synchronizedd' method
        annotationHandlers.put("synchronized", annotationContext::synchronizedd);
        final JexlEngine jexl = new JexlBuilder().annotations(annotationHandlers).create();
        final JexlContext jc = new MapContext();
        jc.set("x", 0);
        final List<String> scripts = new ArrayList<>();

        Object result;
        String scriptText;

        scriptText = "@synchronized { ++x; }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result should be incremented value", 1, result);
        Assert.assertEquals("Annotation count should be 1", 1, annotationContext.getCount());
        Assert.assertTrue("Annotation name set should contain 'synchronized'", annotationContext.getNames().contains("synchronized"));

        scriptText = "@synchronized { ++x }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result should be a singleton set of the incremented value", Collections.singleton(2), result);
        Assert.assertEquals("Annotation count should be 2", 2, annotationContext.getCount());
        Assert.assertTrue("Annotation name set should still contain 'synchronized'", annotationContext.getNames().contains("synchronized"));

        jc.set("x", 0); // Reset for next set of tests

        scriptText = "{ ++x }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result should be a singleton set", Collections.singleton(1), result);

        scriptText = "{ ++x; }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result should be the scalar incremented value", 2, result);

        scriptText = "{'a': 42}['a']";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result should match the accessed map value", 42, result);

        scriptText = "{ var y = 42; }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result should equal the assigned value", 42, result);

        jc.set("p", "parameter");
        scriptText = "@synchronized(p) { var y = 42; }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result should equal assigned value with annotation param", 42, result);
        Assert.assertEquals("Annotation count should be 3", 3, annotationContext.getCount());

        scriptText = "{ var y = 42 }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result should be a singleton set of assigned value", Collections.singleton(42), result);

        scriptText = "@synchronized(p) { var y = 42 }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result should be a singleton set of assigned value", Collections.singleton(42), result);
        Assert.assertEquals("Annotation count should be 4", 4, annotationContext.getCount());

        scriptText = "{ var y = 24; y + 18 }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result should be value of non-terminated statement", 42, result);

        scriptText = "{ 42; }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result of semicolon-terminated expression should be the value", 42, result);

        scriptText = "@synchronized { 42; }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result of annotated, terminated expression should be the value", 42, result);
        Assert.assertEquals("Annotation count should be 5", 5, annotationContext.getCount());

        scriptText = "{ 42 }";
        scripts.add(scriptText);
        result = jexl.createScript(scriptText).execute(jc);
        Assert.assertEquals("Result of non-terminated expression should be a singleton set", Collections.singleton(42), result);

        // Final check: reconstruct all scripts from their syntax trees
        for (final String scriptToCheck : scripts) {
            checkParse(jexl, scriptToCheck);
        }
    }
}

