package org.apache.commons.jexl3;

import org.apache.commons.jexl3.JexlBuilder;
import org.apache.commons.jexl3.JexlContext;
import org.apache.commons.jexl3.JexlEngine;
import org.apache.commons.jexl3.JexlScript;
import org.apache.commons.jexl3.MapContext;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.Assertions;

public class SwitchCaseTest {

    @Test
    public void testSwitchCaseFlow() {
        // Configure the engine for strict error reporting and disabled safe navigation
        JexlEngine jexl = new JexlBuilder().strict(true).safe(false).create();

        // Define the switch-case expression
        String switchExpression =
                "switch(x) {" +
                "  case 1: 100; break;" +
                "  case 2:" +
                "  case 3: 200; break;" +
                "  case 4: break;" +
                "  default: x + 10;" +
                "}" +
                "55;"; // Fallback numeric literal

        // Compile the script
        JexlScript script = jexl.createScript(switchExpression, "x");

        // Verify that the script was parsed and stored
        Assertions.assertNotNull(script.getSourceText(), "The script source should be available.");

        JexlContext context = new MapContext();

        // Test case 1: Direct match
        context.set("x", 1);
        Object result1 = script.execute(context);
        Assertions.assertEquals(100, result1, "Input 1 should result in 100.");

        // Test case 2: First fallthrough value
        context.set("x", 2);
        Object result2 = script.execute(context);
        Assertions.assertEquals(200, result2, "Input 2 should fallthrough to 200.");

        // Test case 3: Second fallthrough value
        context.set("x", 3);
        Object result3 = script.execute(context);
        Assertions.assertEquals(200, result3, "Input 3 should result in 200.");

        // Test case 4: Break and continue to fallback literal
        context.set("x", 4);
        Object result4 = script.execute(context);
        Assertions.assertEquals(55, result4, "Input 4 should break and return the fallback value 55.");

        // Test case 5: Default case
        context.set("x", 5);
        Object result5 = script.execute(context);
        Assertions.assertEquals(15, result5, "Input 5 should trigger the default case and calculate 5 + 10.");
    }
}

package org.apache.commons.jexl3;

import org.apache.commons.jexl3.JexlBuilder;
import org.apache.commons.jexl3.JexlEngine;
import org.apache.commons.jexl3.JexlScript;
import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;

/**
 * Tests for JEXL switch statement behavior.
 */
public class SwitchStatementTest {

    /**
     * Helper method to validate that a script's parsed representation can be recreated into an equivalent script.
     * This mimics a debugger's ability to deconstruct and reconstruct code.
     *
     * @param engine the JEXL engine
     * @param origin the original script
     */
    private void validateDebugger(JexlEngine engine, JexlScript origin) {
        String parsedText = origin.getParsedText();
        assertThat(parsedText).isNotNull().isNotEmpty();

        // Recreate the script from its parsed text representation
        JexlScript recreated = engine.createScript(parsedText, origin.getParameters());
        assertThat(recreated).isNotNull();

        // Assert that the recreated script has the same parsed structure
        assertThat(recreated.getParsedText()).isEqualTo(parsedText);
    }

    @Test
    public void testSwitchStatementWithFallThroughAndBreak() {
        // 1. Configure a JEXL Engine with safe mode disabled and strict mode enabled.
        final JexlEngine jexl = new JexlBuilder().safe(false).strict(true).create();

        // 2. Construct a multi-line script with a switch statement demonstrating various control flows.
        final String scriptSource =
            "switch(x) {
" +
            "  case 1: return 10;
" +
            "  case 2:
" +
            "  case 3: return 20;
" +
            "  case 4: break;
" +
            "  default: return x + 100;
" +
            "}
" +
            "50;"; // This final literal is the return value only when the 'break' is hit.

        // 3. Create the script object, declaring 'x' as a parameter.
        final JexlScript script = jexl.createScript(scriptSource, "x");
        assertThat(script).isNotNull();

        // 4. Retrieve and assert that the parsed text representation is available.
        final String parsedText = script.getParsedText();
        assertThat(parsedText).isNotEmpty();

        // 5. Execute the script with different values to validate each path.

        // Execute with x=1 to test the first explicit case.
        Object result1 = script.execute(null, 1);
        assertThat(result1).isEqualTo(10);

        // Execute with x=2 to test the first fall-through case.
        Object result2 = script.execute(null, 2);
        assertThat(result2).isEqualTo(20);

        // Execute with x=3 to test the second fall-through case.
        Object result3 = script.execute(null, 3);
        assertThat(result3).isEqualTo(20);

        // Execute with x=4 to test the break statement.
        // Control flow should exit the switch and evaluate the final literal.
        Object result4 = script.execute(null, 4);
        assertThat(result4).isEqualTo(50);

        // Execute with x=5 to test the default case.
        Object result5 = script.execute(null, 5);
        assertThat(result5).isEqualTo(105); // 5 + 100

        // 6. Upon completion, perform the debugger validation.
        validateDebugger(jexl, script);
    }
}

