package org.apache.commons.jexl3;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

/**
 * Test case for switch statement syntax and behavior.
 */
public class SwitchStatementTest {

    @Test
    public void testSwitchExpressionSyntaxAndBehavior() {
        // 1. Configure the engine as per requirements
        final JexlFeatures features = new JexlFeatures()
                .ambiguous(true); // Permit ambiguous statement syntax
        final JexlEngine jexl = new JexlBuilder()
                .features(features)
                .safe(false)       // Disable safe navigation
                .strict(true)      // Enforce strict evaluation
                .create();

        // 2. Prepare the valid switch expression
        final String validSwitchExpression = "switch (x) { case 10, 11: 3; case 20, 21: 4; default: x + 4; }";
        final JexlScript script = jexl.createScript(validSwitchExpression);

        // 3. Confirm the system can retrieve its textual representation
        Assertions.assertNotNull(script.getSourceText(), "Parsed representation should be retrievable.");

        final JexlContext context = new MapContext();

        // 4. Execute with input 10 and verify
        context.set("x", 10);
        Assertions.assertEquals(3, script.execute(context), "Input 10 should yield 3.");

        // 5. Execute with input 11 and verify
        context.set("x", 11);
        Assertions.assertEquals(3, script.execute(context), "Input 11 should also yield 3.");

        // 6. Execute with input 20 and verify
        context.set("x", 20);
        Assertions.assertEquals(4, script.execute(context), "Input 20 should yield 4.");

        // 7. Execute with input 21 and verify
        context.set("x", 21);
        Assertions.assertEquals(4, script.execute(context), "Input 21 should also yield 4.");

        // 8. Execute with a default case value and verify
        context.set("x", 38);
        Assertions.assertEquals(42, script.execute(context), "Default should compute 38 + 4 = 42.");

        // 9. Prepare the invalid switch expression with a break statement
        final String invalidSwitchExpression = "switch (x) { case 1: break; }";

        // 10. Attempt to parse and assert that it fails with a specific parsing error
        final JexlException.Parsing parsingException = Assertions.assertThrows(
                JexlException.Parsing.class,
                () -> jexl.createScript(invalidSwitchExpression),
                "Should fail to parse a switch expression with a 'break' statement."
        );

        // 11. Confirm the error message references the prohibited 'break' keyword
        final String errorMessage = parsingException.getMessage();
        Assertions.assertTrue(errorMessage != null && errorMessage.contains("break"),
                "Error message must reference the prohibited 'break' keyword.");
    }
}

package org.apache.commons.jexl3;

import org.apache.commons.jexl3.junit.Asserter;
import org.junit.After;
import org.junit.Assert;
import org.junit.Before;
import org.junit.Test;

/**
 * Tests for switch expression without a default statement.
 */
public class SwitchExpressionTest {

    public enum E {
        A, B
    }

    private JexlEngine jexl;
    private Asserter asserter;

    @Before
    public void setUp() {
        jexl = new JexlBuilder()
                .imports(java.util.Collections.singletonList(SwitchExpressionTest.class.getName()))
                .loader(SwitchExpressionTest.class.getClassLoader())
                .create();
        asserter = new Asserter(jexl);
    }

    @After
    public void tearDown() throws Exception {
        asserter.debuggerCheck(jexl);
    }

    @Test
    public void testSwitchExpressionNoDefault() throws Exception {
        final String switchSrc = "switch(x) { case A: 1; }";
        final JexlScript script = jexl.createScript(switchSrc, "x");
        try {
            script.execute(null, E.B);
            Assert.fail("Switch expression without default should not succeed");
        } catch (final JexlException.Switch xany) {
            Assert.assertTrue("Exception message should contain 'switch'", xany.getMessage().contains("switch"));
        }
    }
}

package org.apache.commons.jexl3;

import java.util.Collections;
import java.util.HashSet;
import java.util.Set;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertTrue;

@SuppressWarnings({"UnnecessaryBoxing", "AssertEqualsBetweenInconvertibleTypes"})
public class AnnotationTest1 {

    static final JexlEngine JEXL = new JexlBuilder().cache(512).create();

    public static class AnnotationContext extends MapContext implements JexlContext.AnnotationProcessor {
        private int count = 0;
        private final Set<String> names = new HashSet<>();

        @Override
        public Object processAnnotation(final String name, final Object[] args, final JexlInfo info) {
            count += 1;
            names.add(name);
            return name;
        }

        public int getCount() {
            return count;
        }

