        assertEquals(3, script.execute(null, 11), "The result for input 11 should be 3");
        assertEquals(4, script.execute(null, 20), "The result for input 20 should be 4");
        assertEquals(4, script.execute(null, 21), "The result for input 21 should be 4");
        assertEquals(42, script.execute(null, 38), "The default branch should compute to 42");

        String invalidBreakSource = "switch (x) { case 10, 11 -> { break; } default -> 12 }";
