public class SwitchExpressionTest {

    public enum TestEnum {
        UNDEFINED,
        THIS
    }

    @Test
    public void testSwitchExpressionWithEnum() {
        // Configure the engine to recognize the test class and its constants
        final JexlEngine jexl = new JexlBuilder()
                .namespaces(Collections.singletonMap("test", TestEnum.class))
                .create();

        // Define the script with a switch expression
        final String scriptText = "(x) -> switch(x) {"
                + " case test:UNDEFINED: 'undefined';"
                + " case test:THIS: 'this';"
                + " default: 'OTHER';"
                + "}";

        final JexlScript script = jexl.createScript(scriptText);
        Assertions.assertNotNull(script, "The script should be successfully created.");
        Assertions.assertNotNull(script.getSourceText(), "The script's textual representation should be available.");

        final JexlContext context = new MapContext();

        // Execute with the first enum constant
        Object result1 = script.execute(context, TestEnum.UNDEFINED);
        Assertions.assertEquals("undefined", result1, "The switch case for UNDEFINED should be correctly evaluated.");

        // Execute with the second enum constant
        Object result2 = script.execute(context, TestEnum.THIS);
        Assertions.assertEquals("this", result2, "The switch case for THIS should be correctly evaluated.");

        // Execute with a value that falls through to the default case
        Object result3 = script.execute(context, 42);
        Assertions.assertEquals("OTHER", result3, "The default case should be executed for an unrecognized input.");
    }
