public class EnumConditionalTest {

    public enum TestEnum {
        A, B
    }

    @Test
    public void testEnumConditionalLogic() throws Exception {
        // Configure engine to import the enum
        JexlEngine jexl = new JexlBuilder()
                .imports(Collections.singleton(TestEnum.class.getName()))
                .create();

        // Define a routine with a conditional branch based on the enum
        String scriptText = "(value) -> { if (value == TestEnum.A) { return 'matched A'; } }";
        JexlScript script = jexl.createScript(scriptText, "value");

        // Execute with matching value and verify the result
        Object result = script.execute(null, TestEnum.A);
        assertEquals("matched A", result, "Should return 'matched A' for TestEnum.A");

        // Execute with a non-matching value that has no 'else' block
        JexlException.Conditional exception = assertThrows(JexlException.Conditional.class, () -> {
            script.execute(null, TestEnum.B);
        });

        // Verify the error message indicates the failure point
        String errorMessage = exception.getMessage();
        assertTrue(errorMessage.contains("if"), "Error message should reference the 'if' construct that failed");
    }
}

