import java.nio.file.Files;
import java.nio.file.Path;
import java.util.Arrays;
import java.util.Collection;

import static org.junit.jupiter.api.Assertions.assertArrayEquals;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertInstanceOf;
import static org.junit.jupiter.api.Assertions.assertNotNull;
import static org.junit.jupiter.api.Assertions.assertTrue;

public class MultilineExpressionTest {

    @Test
    public void testMultilineTemplateLiteralsAndFunctions(@TempDir Path tempDir) throws Exception {
        // Initialize engine
        final JexlEngine jexl = new JexlBuilder().create();

        // Prepare script with a constant, a function, template literals, and conditional operators
        final String scriptText = "const aKey = 'A';
"
                + "const CONST_LIT = `1
2
${aKey == 'A' ? '3' : '4'}`;
"
                + "function func(p1, p2) { `Hello ${p1} and ${p2}!` };
"
                + "`${func(param1, param2)} ${CONST_LIT}`";

        // Parse the script specifying input parameters
        final JexlScript script = jexl.createScript(scriptText, "param1", "param2");

        // Execute with sample values
        final Object result = script.execute(null, "World", "JEXL");

        // Verify the output
        final String expectedOutput = "Hello World and JEXL! 1
2
3";
        assertEquals(expectedOutput, result.toString());

        // Confirm declared variables and functions
        final String[] localVariables = script.getLocalVariables();
        assertNotNull(localVariables);
        Arrays.sort(localVariables);
        assertArrayEquals(new String[]{"CONST_LIT", "aKey", "func"}, localVariables);

        // Prepare a validation script to be loaded from a file
        final String validationScriptText =
                "[" +
                " 1 + 1 == 2," +
                " 'foo' + 'bar' == 'foobar'," +
                " `template` == 'template'," +
                " (true ? 1 : 2) == 1" +
                "]";

        // Write script to a temporary file
        Path scriptFile = tempDir.resolve("validation.jexl");
        Files.write(scriptFile, validationScriptText.getBytes());

        // Load the script from the file system
        final JexlScript fileScript = jexl.createScript(scriptFile.toFile());

        // Execute and verify it returns a collection of boolean outcomes
        final Object fileResult = fileScript.execute(null);
        assertInstanceOf(Collection.class, fileResult, "Result should be a collection");

        // Verify each validation result evaluates to true
        final Collection<?> results = (Collection<?>) fileResult;
        for (final Object item : results) {
            assertInstanceOf(Boolean.class, item, "Each item in the collection should be a Boolean");
            assertTrue((Boolean) item, "Each validation case should pass");
        }
    }
}

