        // Construct a multi-line source string with template literals, a constant, and a function
        final String source = "const TOTO = 42;\n"
                            + "var fun = (x, y) -> `x = ${x}, y = ${y}`;\n"
                            + "`Hello ${arg1}, ${arg2}! ${fun(10, 'ten')}`";

