        final JexlScript script = jexl.createScript(source, "arg1", "arg2");

        // Execute the script with null context and string arguments
        final Object result = script.execute(null, "jexl", "works");

        // Validate the interpolated result
        Assertions.assertEquals("Hello jexl, works! x = 10, y = ten", result);

        // Retrieve and verify the script's local variables in the expected order
        final String[] localVariables = script.getLocalVariables();
        Assertions.assertNotNull(localVariables);
        Assertions.assertEquals(2, localVariables.length);
