package org.apache.commons.jexl3;

import org.apache.commons.jexl3.JexlBuilder;
import org.apache.commons.jexl3.JexlContext;
import org.apache.commons.jexl3.JexlEngine;
import org.apache.commons.jexl3.JexlExpression;
import org.apache.commons.jexl3.JexlFeatures;
import org.apache.commons.jexl3.MapContext;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

/**
 * Tests SQL-style comparison operators under restricted features.
 */
public class SqlStyleComparisonTest {

    @Test
    public void testSqlStyleOperators() {
        // Configure the engine with restrictive features to emulate an SQL-like environment
        final JexlFeatures features = new JexlFeatures()
                .localVariable(false)
                .lambda(false)
                .loops(false)
                .sideEffect(false)
                .sideEffectGlobal(false);

        // Initialize the engine with a cache and the specified features
        final JexlEngine jexl = new JexlBuilder()
                .features(features)
                .cache(32)
                .create();

        // Prepare expressions for different comparison operators
        final JexlExpression neExpr = jexl.createExpression("x <> 25");
        final JexlExpression eqExpr = jexl.createExpression("x = 25");
        final JexlExpression bangNeExpr = jexl.createExpression("x != 25");

        final JexlContext context = new MapContext();

        // Test inequality with <>
        context.set("x", 24);
        Assertions.assertTrue((Boolean) neExpr.evaluate(context), "24 <> 25 should be true");

        context.set("x", 25);
        Assertions.assertFalse((Boolean) neExpr.evaluate(context), "25 <> 25 should be false");

        // Test equality with =
        context.set("x", 24);
        Assertions.assertFalse((Boolean) eqExpr.evaluate(context), "24 = 25 should be false");

        // Test inequality with !=
        Assertions.assertTrue((Boolean) bangNeExpr.evaluate(context), "24 != 25 should be true");

        // Re-test equality and inequality with a matching value
        context.set("x", 25);
        Assertions.assertTrue((Boolean) eqExpr.evaluate(context), "25 = 25 should be true");
        Assertions.assertFalse((Boolean) bangNeExpr.evaluate(context), "25 != 25 should be false");
    }
}

