import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertTrue;

public class SqlStyleComparisonsTest {

    @Test
    public void testSqlStyleComparisons() {
        JexlFeatures features = new JexlFeatures()
                .localVariable(false)
                .lambda(false)
                .loops(false)
                .sideEffect(false)
                .sideEffectGlobal(false);

        // The prompt refers to a "custom SQL parser factory" which is not available in the JexlBuilder API.
        // This test will use the standard JEXL operators that are equivalent to SQL operators.
        JexlEngine jexl = new JexlBuilder()
                .cache(32)
                .features(features)
                .create();

        // Test for SQL's <> operator (using JEXL's !=)
        JexlScript scriptNE = jexl.createScript("x != 10", "x");
        assertTrue((Boolean) scriptNE.execute(null, 5));
        assertFalse((Boolean) scriptNE.execute(null, 10));

        // Test for SQL's = operator (using JEXL's ==)
        JexlScript scriptEQ = jexl.createScript("x == 10", "x");
        assertFalse((Boolean) scriptEQ.execute(null, 5));
        assertTrue((Boolean) scriptEQ.execute(null, 10));

        // Test for SQL's != operator (using JEXL's !=)
        JexlScript scriptNE2 = jexl.createScript("x != 20", "x");
        assertTrue((Boolean) scriptNE2.execute(null, 10));
        assertFalse((Boolean) scriptNE2.execute(null, 20));
    }
}

package org.apache.commons.jexl3;

import org.apache.commons.jexl3.internal.Engine;
import org.junit.jupiter.api.Test;
import org.assertj.core.api.Assertions;

import java.io.StringWriter;

/**
 * Tests for template caching behavior.
 */
public class TemplateCacheTest {

    @Test
    public void testTemplateComponentReuse() throws Exception {
        // Configure an engine with a small cache and strict template handling.
        // The cache size of 4 is arbitrary but sufficient for this test.
        final JexlEngine jexl = new JexlBuilder().cache(4).strict(true).create();
        final JxltEngine jxlt = jexl.createJxltEngine();
        final String templateText = "${x}${x}";
        final String[] parms = {"x"};

        // Access the underlying cache to monitor its size.
        // This requires casting to the internal Engine implementation.
        final JexlCache<?, ?> cache = ((Engine) jexl).getCache();
        Assertions.assertThat(cache.size()).as("Cache should be empty initially").isEqualTo(0);

        // --- First Iteration ---
        // Create and evaluate the template.
        final JxltEngine.Template template1 = jxlt.createTemplate(templateText, parms);
        Assertions.assertThat(template1).isNotNull();

        final JexlContext context = new MapContext();
        context.set("x", 42);
        final StringWriter writer1 = new StringWriter();
        template1.evaluate(context, writer1);

        // Verify the output and cache state.
        // The cache should contain 2 entries: one for the combined template structure
        // and one for the reused `${x}` part.
        Assertions.assertThat(writer1.toString()).isEqualTo("4242");
        Assertions.assertThat(cache.size()).as("Cache size after first evaluation").isEqualTo(2);

        // --- Second Iteration ---
        // Re-create and evaluate the same template to test caching.
        final JxltEngine.Template template2 = jxlt.createTemplate(templateText, parms);
        Assertions.assertThat(template2).isNotNull();

        final StringWriter writer2 = new StringWriter();
        template2.evaluate(context, writer2);

        // Verify the output is the same and the cache size has not grown,
        // demonstrating that the parsed components were reused.
        Assertions.assertThat(writer2.toString()).isEqualTo("4242");
        Assertions.assertThat(cache.size())
                .as("Cache size should remain stable on second evaluation, indicating component reuse")
                .isEqualTo(2);
    }
}

