package org.apache.commons.jexl3;

import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;

public class ReferenceCaptureTest {

    @Test
    public void testInnerFunctionModifiesOuterScopeVariable() {
        // Configure the system to enable reference capture semantics (read-write variables).
        JexlOptions options = new JexlOptions();
        options.setConstCapture(false); // allow modification of captured variables
        JexlEngine jexl = new JexlBuilder().options(options).create();

        // Script establishes a variable, an incrementor, and a function that reassigns
        // the variable and returns a new function that calls the incrementor.
        String scriptText =
                "var x = 40;" +
                "var f = () -> { x = x + 2; };" +
                "var g = () -> {" +
                "  x = -169;" +
                "  return () -> { f(); return x; };" +
                "};" +
                "g();";

        JexlScript script = jexl.createScript(scriptText);

        // Execute the outer script to get the callable inner function.
        Object result = script.execute(null);

        // Confirm the system produces a callable function.
        assertThat(result).isInstanceOf(JexlScript.class);
        JexlScript innerFunction = (JexlScript) result;

        // Invoke the returned function and verify the final computed value.
        Object finalValue = innerFunction.execute(null);

        // The value should be -169 (reassigned) + 2 (incremented).
        assertThat(finalValue).isEqualTo(-167);
    }
}

