import static org.junit.jupiter.api.Assertions.assertInstanceOf;
import static org.junit.jupiter.api.Assertions.assertNotNull;

@SuppressWarnings({"AssertEqualsBetweenInconvertibleTypes"})
public class ReferenceCaptureTest {

    @Test
    public void testReferenceCapture() {
        final JexlFeatures f426 = new JexlFeatures().referenceCapture(true);
        final JexlEngine jexl = new JexlBuilder().features(f426).create();
        String src = "let x = 40;\n    function foo()\n    {\n        x += 2;\n    }\n    ;\n    function bar()\n    {\n        x = -169;\n        () ->\n        {\n            foo();\n        }\n    }\n    ;\n    bar();\n    ";
        JexlScript script = jexl.createScript(src);
        assertNotNull(script);
        Object result = script.execute(null);
        assertInstanceOf(JexlScript.class, result);
        script = jexl.createScript("f()", "f");
        result = script.execute(null, result);
        Assertions.assertEquals(-167, result);
    }
}

