package org.apache.commons.jexl3;

import org.apache.commons.jexl3.internal.Scope;
import org.apache.commons.jexl3.parser.ASTJexlScript;
import org.apache.commons.jexl3.parser.JexlParser;
import org.apache.commons.jexl3.parser.JexlScriptParser;
import org.apache.commons.jexl3.parser.Parser;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

public class SQLParserTest {

    static final char[] EQ_FRIEND = new char[0];

    public static class SQLParser implements JexlScriptParser {
        @Override
        public ASTJexlScript parse(final JexlInfo info, final JexlFeatures features, final String src, final Scope scope) {
            String fixedSrc = src.replaceAll("<>", "!=").replaceAll("=", "==");
            Parser parser = new Parser((JexlParser) null);
            return parser.parse(info, features, fixedSrc, scope);
        }
    }

    @Test
    public void testSqlLikeOperators() throws Exception {
        final JexlFeatures f = new JexlFeatures()
                .localVar(false)
                .lambda(false)
                .loops(false)
                .sideEffect(false)
                .sideEffectGlobal(false);
        final JexlBuilder builder = new JexlBuilder()
                .parserFactory(SQLParser::new)
                .cache(32)
                .features(f);
        final JexlEngine sqle = builder.create();

        JexlScript script;

        script = sqle.createScript("a <> 25", "a");
        Assertions.assertTrue((Boolean) script.execute(null, 24));

        script = sqle.createScript("a <> 25", "a");
        Assertions.assertFalse((Boolean) script.execute(null, 25));

        script = sqle.createScript("a = 25", "a");
        Assertions.assertFalse((Boolean) script.execute(null, 24));

        script = sqle.createScript("a != 25", "a");
        Assertions.assertTrue((Boolean) script.execute(null, 24));

        script = sqle.createScript("a = 25", "a");
        Assertions.assertTrue((Boolean) script.execute(null, 25));

        script = sqle.createScript("a != 25", "a");
        Assertions.assertFalse((Boolean) script.execute(null, 25));
    }
}

