package org.apache.commons.jexl3;

import org.apache.commons.jexl3.internal.Debugger;
import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;

/**
 * Tests for reference-capture semantics in lambda functions.
 * This test corresponds to the behavior described in the test description.
 */
public class LambdaReferenceCaptureTest {

    /**
     * Helper method to perform debugger validation on the JexlEngine instance.
     * This ensures the internal consistency of cached expression representations after script execution.
     * @param engine The JexlEngine to validate.
     */
    private void check(final JexlEngine engine) {
        new Debugger().validate(engine);
    }

    @Test
    public void testReferenceCaptureInLambdas() {
        // 1. Configure a JexlEngine with reference-capture enabled through JexlFeatures.
        final JexlFeatures features = new JexlFeatures().referenceCapture(true);
        final JexlEngine jexl = new JexlBuilder().features(features).create();

        // 2. Construct a multi-stage script where nested lambda closures modify captured variables.
        final String scriptSource =
                "var x = 100;" +
                "var modifier = () -> { x = x - 20; };" +
                "var outer = () -> {" +
                "    modifier();" +
                "    return () -> { x = x + 5; };" +
                "};" +
                "outer();";

        // 3. Parse and execute the script to obtain the inner lambda closure.
        final JexlScript script = jexl.createScript(scriptSource);
        assertThat(script).isNotNull();

        final Object result = script.execute(null);

        // 4. Assert that the execution result is a JexlScript, confirming the lambda was captured.
        assertThat(result).isInstanceOf(JexlScript.class);
        final JexlScript innerLambda = (JexlScript) result;

        // 5. Create a second script that accepts the lambda as a parameter and invokes it.
        final String wrapperScriptSource = "(lambda) -> { lambda(); return x; }";
        final JexlScript wrapperScript = jexl.createScript(wrapperScriptSource);

        // 6. Execute the wrapper, passing the captured lambda, and validate the final result.
        // The initial value of x (100) is modified to 80 by modifier(), then the inner lambda
        // adds 5, resulting in 85. This confirms the inner lambda captured the reference to x
        // and observed the modification by the intermediate function call.
        final Object finalResult = wrapperScript.execute(null, innerLambda);
        assertThat(finalResult).isEqualTo(85);

        // 7. Perform debugger validation to ensure internal consistency.
        check(jexl);
    }
}

package org.apache.commons.jexl3.internal;

import java.util.HashMap;
import org.apache.commons.jexl3.JexlBuilder;
import org.apache.commons.jexl3.JexlCache;
import org.apache.commons.jexl3.JexlEngine;
import org.apache.commons.jexl3.JexlFeatures;
import org.apache.commons.jexl3.JexlScript;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertNotEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;

public class SourceTest {

    @Test
    public void testSourceFeaturesAndSymbols() {
        final JexlFeatures features = JexlFeatures.createDefault();
        final HashMap<String, Integer> symbols0 = new HashMap<>();
        symbols0.put("x", 0);
        symbols0.put("y", 1);
        final Source src0 = new Source(features, symbols0, "x + y");
        assertFalse(src0.equals(null));
        assertFalse(src0.equals("x + y"));
        final HashMap<String, Integer> symbols1 = new HashMap<>();
        symbols0.put("x", 0);
        symbols0.put("y", 2);
        final Source src1 = new Source(features, symbols1, "x + y");
        assertNotEquals(src0, src1);
        assertNotEquals(0, src0.compareTo(src1));
        final Source src2 = new Source(features, null, "x + y");
        assertNotEquals(src0, src2);
        assertNotEquals(0, src0.compareTo(src2));
        final Source src3 = new Source(JexlFeatures.createNone(), symbols1, "x + y");
        assertNotEquals(src0, src3);
        assertNotEquals(0, src0.compareTo(src3));
        final JexlEngine jexl = new JexlBuilder().cache(4).create();
        final JexlCache<Source, Object> cache = ((Engine) jexl).getCache();
        JexlScript script0 = jexl.createScript("x + y", "x", "y");
        assertNotNull(script0);
        JexlScript script1 = jexl.createScript("x + y", "y", "x");
        assertNotNull(script1);
        assertEquals(2, cache.size());
        cache.clear();
        script0 = jexl.createScript("x + y", "x", "y");
        assertNotNull(script0);
        script1 = jexl.createScript("x + y", "x", "y");
        assertNotNull(script1);
        assertEquals(1, cache.size());
    }
}

package org.apache.commons.jexl3.internal;

import org.apache.commons.jexl3.JexlBuilder;
import org.apache.commons.jexl3.JexlCache;
import org.apache.commons.jexl3.JexlEngine;
import org.apache.commons.jexl3.JexlFeatures;
import org.apache.commons.jexl3.JexlScript;
import org.junit.jupiter.api.Test;

import java.util.LinkedHashMap;
import java.util.Map;

import static org.assertj.core.api.Assertions.assertThat;

/**
 * Tests the caching behavior of JexlEngine based on source variations.
 * This test is placed in the internal package to allow direct instantiation
 * of the Source class, as required to fulfill the test description.
 */
public class ScriptCacheSourceTest {

    @Test
    public void testCachingBehaviorWithSourceMetadataVariations() {
        // Part 1: Validate Source equality and comparison semantics as cache keys.
        final JexlFeatures features = new JexlFeatures();
        final Map<String, Integer> symbols1 = new LinkedHashMap<>();
        symbols1.put("x", 0);
        symbols1.put("y", 1);
        final Source source1 = new Source("test.jexl", "x + y", symbols1, features);

        // Assert basic equality checks
        assertThat(source1).isNotEqualTo(null);
        assertThat(source1).isNotEqualTo("x + y");

        // Create a second source with a different symbol map (distinct position)
        final Map<String, Integer> symbols2 = new LinkedHashMap<>();
        symbols2.put("x", 0);
        symbols2.put("y", 2); // Different position for 'y'
        final Source source2 = new Source("test.jexl", "x + y", symbols2, features);
        assertThat(source1).isNotEqualTo(source2);
        assertThat(source1.compareTo(source2)).isNotZero();

        // Create a third source with a null symbol map
        final Source source3 = new Source("test.jexl", "x + y", null, features);
        assertThat(source1).isNotEqualTo(source3);
        assertThat(source1.compareTo(source3)).isNotZero();

        // Create a fourth source with a different feature set
        final JexlFeatures features4 = new JexlFeatures().strict(false);
        final Source source4 = new Source("test.jexl", "x + y", symbols1, features4);
        assertThat(source1).isNotEqualTo(source4);
        assertThat(source1.compareTo(source4)).isNotZero();

        // Part 2: Validate engine caching behavior using the differentiated sources.
        final JexlEngine engine = new JexlBuilder().cache(4).create();
        final Engine internalEngine = (Engine) engine;
        final JexlCache<Source, Script> cache = internalEngine.cache;
        assertThat(cache).isNotNull();

        // Create two scripts from the same expression but with parameters in a different order.
        // This results in different Source instances being generated internally.
        engine.createScript("x + y", "x", "y");
        engine.createScript("x + y", "y", "x");

        // Verify the cache contains two distinct entries because the Source signatures differ.
        assertThat(cache.size()).isEqualTo(2);

        // Clear the cache and create two scripts with identical expressions and parameter order.
        engine.clearCache();
        assertThat(cache.size()).isZero();

        engine.createScript("z * w", "z", "w");
        engine.createScript("z * w", "z", "w");

        // Verify the cache now contains only one entry because the Sources are equivalent.
        assertThat(cache.size()).isEqualTo(1);
    }
}

