package org.apache.commons.jexl3;

import org.junit.jupiter.api.Test;
import java.lang.reflect.Field;
import java.util.ArrayList;
import java.util.List;
import java.util.Set;

import static org.assertj.core.api.Assertions.assertThat;

/**
 * Tests for source and cache interaction.
 */
public class SourceConfigurationTest {

    @SuppressWarnings("unchecked")
    private Set<Object> getCacheKeySet(final JexlEngine jexl) throws Exception {
        final Field cacheField = jexl.getClass().getDeclaredField("cache");
        cacheField.setAccessible(true);
        final JexlCache<Object, ?> cache = (JexlCache<Object, ?>) cacheField.get(jexl);
        return cache.keySet();
    }

    @Test
    public void testSourceConfigurationDistinction() throws Exception {
        final String expr = "y + z";

        // Baseline source configuration
        final JexlEngine jexlDefault = new JexlBuilder().create();
        final JexlFeatures featuresDefault = jexlDefault.getFeatures();
        final Object source1;
        {
            jexlDefault.createScript(expr, "y", "z");
            Set<Object> keys = getCacheKeySet(jexlDefault);
            assertThat(keys).hasSize(1);
            source1 = keys.iterator().next();
        }

        // Verify baseline rejects comparison with incompatible types
        assertThat(source1).isNotEqualTo("plain text string");
        assertThat(source1).isNotNull();

        // Alternative source configuration with different variable position
        final Object source2;
        {
            jexlDefault.createScript(expr, "z", "y");
            Set<Object> keys = getCacheKeySet(jexlDefault);
            assertThat(keys).hasSize(2);
            List<Object> keyList = new ArrayList<>(keys);
            source2 = keyList.get(0).equals(source1) ? keyList.get(1) : keyList.get(0);
        }

        // Confirm the two configurations are distinct
        assertThat(source1).isNotEqualTo(source2);
        assertThat((Comparable<Object>) source1).isNotEqualByComparingTo(source2);

        // Third source configuration with no variable mappings
        final Object source3;
        {
            jexlDefault.createScript(expr);
            Set<Object> keys = getCacheKeySet(jexlDefault);
            assertThat(keys).hasSize(3);
            keys.remove(source1);
            keys.remove(source2);
            source3 = keys.iterator().next();
        }
        assertThat(source1).isNotEqualTo(source3);

        // Fourth source configuration with restricted features
        final JexlFeatures featuresRestricted = new JexlFeatures().script(false);
        final JexlEngine jexlRestricted = new JexlBuilder().features(featuresRestricted).create();
        final Object source4;
        {
            jexlRestricted.createScript(expr, "y", "z");
            Set<Object> keys = getCacheKeySet(jexlRestricted);
            assertThat(keys).hasSize(1);
            source4 = keys.iterator().next();
        }
        assertThat(source1).isNotEqualTo(source4);

        // Cache behavior with different variable declaration orders
        final JexlEngine jexlWithCache = new JexlBuilder().cache(4).create();
        JexlScript scriptYz = jexlWithCache.createScript(expr, "y", "z");
        JexlScript scriptZy = jexlWithCache.createScript(expr, "z", "y");

        assertThat(getCacheKeySet(jexlWithCache)).hasSize(2);
        assertThat(scriptYz).isNotSameAs(scriptZy);

        // Clear cache and verify reuse
        jexlWithCache.clearCache();
        assertThat(getCacheKeySet(jexlWithCache)).isEmpty();

        JexlScript script1 = jexlWithCache.createScript(expr, "y", "z");
        JexlScript script2 = jexlWithCache.createScript(expr, "y", "z");

        assertThat(getCacheKeySet(jexlWithCache)).hasSize(1);
        assertThat(script1).isSameAs(script2);
    }
}
