package org.apache.commons.text.lookup;

import static org.assertj.core.api.Assertions.assertThat;

import javax.xml.XMLConstants;

import org.apache.commons.text.StringSubstitutor;
import org.junit.jupiter.api.Test;

/**
 * Tests {@link StringSubstitutor} with {@link XmlStringLookup} to validate
 * interpolation from structured documents with external entity access enabled.
 */
public class XmlStringLookupWithExternalEntityTest {

    private static final String STRUCTURED_DOCUMENT_PATH = "src/site/xdoc/index.xml";
    private static final String XPATH_EXPRESSION = "/document/body/section/@name";
    private static final String EXPECTED_CONTENT = "Apache Commons Text";

    @Test
    void testXmlLookupWithFileBasedExternalEntityResolution() {
        final String originalAccessExternalDtd = System.getProperty(XMLConstants.ACCESS_EXTERNAL_DTD);
        try {
            // Configure the runtime to allow file-based external entity resolution.
            System.setProperty(XMLConstants.ACCESS_EXTERNAL_DTD, "file");

            // Initialize the interpolation system with its standard set of variable resolvers.
            final StringSubstitutor substitutor = StringSubstitutor.createInterpolator();

            // Construct a variable reference expression that points to a structured document file.
            final String variableReference = String.format("${xml:%s:%s}", STRUCTURED_DOCUMENT_PATH, XPATH_EXPRESSION);

            // Submit this expression to the interpolation system for processing.
            final String resolvedValue = substitutor.replace(variableReference);

            // Verify that the system successfully resolves the reference by extracting the expected content.
            assertThat(resolvedValue.trim()).isEqualTo(EXPECTED_CONTENT);
        } finally {
            // Restore original system property to ensure test isolation.
            if (originalAccessExternalDtd == null) {
                System.clearProperty(XMLConstants.ACCESS_EXTERNAL_DTD);
            } else {
                System.setProperty(XMLConstants.ACCESS_EXTERNAL_DTD, originalAccessExternalDtd);
            }
        }
    }
}

