package org.apache.commons.text;

import static org.junit.jupiter.api.Assertions.assertThrows;

import org.junit.jupiter.api.Test;

/**
 * Tests {@link StringSubstitutor} with default settings.
 */
class StringSubstitutorWithDefaultsTest {

    private static final String DOCUMENT_DIRECTORY = "src/test/resources/org/apache/commons/text/";

    @Test
    void testInterpolatorWithExternalDtdDisabled() {
        final StringSubstitutor substitutor = StringSubstitutor.createInterpolator();
