}

package org.apache.commons.text;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertThrows;

/**
 * Test class for {@link StringSubstitutor} security features.
 */
public class StringSubstitutorSecurityTest {

    /**
     * Tests that the default interpolator configuration rejects external entity references in XML lookups.
     * This is a security check to prevent XXE (XML External Entity) attacks.
     */
    @Test
    public void testInterpolatorRejectsExternalEntityProcessing() {
        // The standard interpolator includes multiple lookups, including one for XML.
        final StringSubstitutor substitutor = StringSubstitutor.createInterpolator();

        // This key attempts to resolve a variable by parsing an XML file from the test resources.
        // The 'document-entity-ref.xml' file is specifically crafted to contain an external entity reference.
        final String key = "xml:org/apache/commons/text/document-entity-ref.xml";

        // When the substitutor attempts to replace the placeholder, the XML lookup should parse the file.
        // The security settings should prevent the processing of external entities, resulting in an exception.
        assertThrows(IllegalArgumentException.class, () -> {
            substitutor.replace("${" + key + "}");
        }, "Expected an IllegalArgumentException because external entity processing should be disabled for security.");
    }
}

package org.apache.commons.text;

import static org.junit.jupiter.api.Assertions.assertThrows;

import org.junit.jupiter.api.Test;

public class StringSubstitutorSecurityTest {

    private static final String DOCUMENT_DIRECTORY_PATH = "src/test/resources/org/apache/commons/text/";
    private static final String DOCUMENT_FILENAME_WITH_ENTITY_REFERENCES = "document-entity-ref.xml";

    @Test
    public void testInterpolatorRejectsExternalEntityResolutionByDefault() {
        final StringSubstitutor substitutor = StringSubstitutor.createInterpolator();

        final String variablePrefix = "${";
        final String xmlLookupKeyIdentifier = "xml";
        final String xPathSelector = "/element";
        final String variableSuffix = "}";

        // Construct an interpolation expression that attempts to reference an XML document entity.
        final String interpolationExpression = variablePrefix + xmlLookupKeyIdentifier + ":" +
            DOCUMENT_DIRECTORY_PATH + DOCUMENT_FILENAME_WITH_ENTITY_REFERENCES + ":" +
            xPathSelector + variableSuffix;

        // Expect the operation to be rejected with an IllegalArgumentException, confirming
        // that the default configuration prevents resolution of external XML entities.
        assertThrows(IllegalArgumentException.class, () -> {
            substitutor.replace(interpolationExpression);
        });
    }
}

