package org.apache.commons.text.lookup;

import static org.assertj.core.api.Assertions.assertThat;

import java.net.URISyntaxException;
import java.nio.file.Paths;

import org.apache.commons.text.StringSubstitutor;
import org.junit.jupiter.api.Test;

/**
 * Tests that the {@link XmlStringLookup} can resolve external entities when
 * configured to do so via a system property. This test validates a security-sensitive feature.
 */
class XmlStringLookupExternalEntityTest {

    /**
     * The name of the system property used to control access to external DTDs and entities.
     * The value "file" permits loading resources from the filesystem.
     */
    private static final String ACCESS_EXTERNAL_DTD_PROPERTY = "org.apache.commons.text.lookup.xml.accessExternalDtd";

    @Test
    void testExternalEntityResolutionIsSuccessfulWhenAccessIsGranted() throws URISyntaxException {
        final String originalValue = System.getProperty(ACCESS_EXTERNAL_DTD_PROPERTY);
        try {
            // Enable file-based external DTD access. This is a security-sensitive setting
            // and should only be enabled when the source of the XML documents is trusted.
            System.setProperty(ACCESS_EXTERNAL_DTD_PROPERTY, "file");

            // A new interpolator is created to ensure it picks up the modified system property.
            final StringSubstitutor substitutor = StringSubstitutor.createInterpolator();

            // The test document 'document-external-dtd.xml' is expected to reference an
            // external DTD ('document-external-dtd.dtd') that defines an entity.
            final String docPath = Paths.get(getClass().getResource("/org/apache/commons/text/document-external-dtd.xml").toURI()).toString();

            // The placeholder requests the value of the '/root/element' node. This node's content
            // is expected to be defined by an entity in the external DTD.
            final String substituted = substitutor.replace("${xml:" + docPath + ":/root/element}");

            // The value "Apache" is the expected content of the entity defined in the external DTD.
            // The result is trimmed to remove any potential whitespace from the XML parsing process.
            assertThat(substituted.trim()).isEqualTo("Apache");
        } finally {
            // Restore the original system property to ensure test isolation and avoid side effects.
            if (originalValue != null) {
                System.setProperty(ACCESS_EXTERNAL_DTD_PROPERTY, originalValue);
            } else {
                System.clearProperty(ACCESS_EXTERNAL_DTD_PROPERTY);
            }
        }
    }
}

package org.apache.commons.text.translate;

import org.junit.jupiter.api.Test;
import org.apache.commons.text.translate.AggregateTranslator;
import org.apache.commons.text.translate.CharSequenceTranslator;

import java.io.IOException;
import java.io.Writer;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertInstanceOf;

/**
 * Tests for {@link CharSequenceTranslator}.
 */
public class CharSequenceTranslatorTest1 {

    private int invocationCount = 0;

    @Test
    public void testWithComposition() throws IOException {
        final CharSequenceTranslator translator1 = new CharSequenceTranslator() {
            @Override
            public int translate(final CharSequence input, final int index, final Writer out) {
                invocationCount++;
                return 0;
            }
        };
        final CharSequenceTranslator translator2 = new CharSequenceTranslator() {
            @Override
            public int translate(final CharSequence input, final int index, final Writer out) {
                invocationCount++;
                return 0;
            }
        };
        final CharSequenceTranslator translator3 = new CharSequenceTranslator() {
            @Override
            public int translate(final CharSequence input, final int index, final Writer out) {
                invocationCount++;
                return 0;
            }
        };

        final CharSequenceTranslator aggregatedTranslator = translator1.with(translator2, translator3);

        assertInstanceOf(AggregateTranslator.class, aggregatedTranslator,
                "The 'with' method should produce an AggregateTranslator instance.");

        aggregatedTranslator.translate("", 0, null);

        assertEquals(3, invocationCount,
                "The aggregated translator should invoke all three composed translators.");
    }
}

package org.apache.commons.text.translate;

import java.io.IOException;
import java.io.StringWriter;
import java.io.Writer;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

/**
 * Test case for {@link AggregateTranslator}.
 */
public class AggregateTranslatorTest1 {

    /**
     * A helper {@link CharSequenceTranslator} that counts its invocations.
     */
    private static class CountingTranslator extends CharSequenceTranslator {
        private int invocationCount = 0;

        @Override
        public int translate(final CharSequence input, final int index, final Writer out) throws IOException {
            invocationCount++;
            return 0; // Don't consume any characters
        }

        public int getInvocationCount() {
            return invocationCount;
        }
    }

    @Test
    public void testRuleAggregationAndInvocation() throws IOException {
        // Establish three separate text transformation rules
        final CountingTranslator rule1 = new CountingTranslator();
        final CountingTranslator rule2 = new CountingTranslator();
        final CountingTranslator rule3 = new CountingTranslator();

        // Combine them into a single unified transformation process
        final CharSequenceTranslator combinedTranslator = new AggregateTranslator(rule1, rule2, rule3);

        // Confirm that the combined process uses the appropriate aggregation strategy
        Assertions.assertTrue(combinedTranslator instanceof AggregateTranslator,
                "The combined transformation process should be an instance of AggregateTranslator.");

        // Trigger transformation with empty input to verify delegation
        final Writer out = new StringWriter();
        combinedTranslator.translate("", 0, out);

        // Verify that all individual transformation rules were invoked
        Assertions.assertEquals(1, rule1.getInvocationCount(), "Rule 1 should have been invoked once.");
        Assertions.assertEquals(1, rule2.getInvocationCount(), "Rule 2 should have been invoked once.");
        Assertions.assertEquals(1, rule3.getInvocationCount(), "Rule 3 should have been invoked once.");
    }
}

package org.apache.commons.text.translate;

import org.junit.jupiter.api.Test;

import java.io.IOException;
import java.io.Writer;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertInstanceOf;

/**
 * Tests {@link CharSequenceTranslator} with method.
 */
public class CharSequenceTranslatorWithTest {

    private int translateInvocationCounter;

    @Test
    public void testWith() throws IOException {
        final CharSequenceTranslator charSequenceTranslatorOne = new CharSequenceTranslator() {
            @Override
            public int translate(final CharSequence input, final int index, final Writer out) {
                translateInvocationCounter++;
                return 0;
            }
        };
        final CharSequenceTranslator charSequenceTranslatorTwo = new CharSequenceTranslator() {
            @Override
            public int translate(final CharSequence input, final int index, final Writer out) {
                translateInvocationCounter++;
                return 0;
            }
        };
        final CharSequenceTranslator charSequenceTranslatorThree = new CharSequenceTranslator() {
            @Override
            public int translate(final CharSequence input, final int index, final Writer out) {
                translateInvocationCounter++;
                return 0;
            }
