package org.apache.commons.text;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;

import org.junit.jupiter.api.Test;

/**
 * Unit tests for {@link TextStringBuilder}.
 */
public class TextStringBuilderTest1 {

    /**
     * The default capacity for a {@code TextStringBuilder}. This is often defined in a base class like {@code StrBuilder}.
     */
    private static final int DEFAULT_CAPACITY = 32;

    @Test
    public void testEmptyConstructorInitialState() {
        // Initialize the builder without any content.
        final TextStringBuilder builder = new TextStringBuilder();

        // Ensure the builder's internal storage capacity matches the standard initial allocation size.
        assertEquals(DEFAULT_CAPACITY, builder.capacity(),
                "The builder's internal storage capacity should match the standard initial allocation size.");

        // Verify that the content is empty.
        assertEquals(0, builder.length(), "The builder's length should be zero, indicating no content.");

        // Verify that when requesting the current content as a character representation,
        // the system returns an empty collection with zero elements.
        final char[] chars = builder.toCharArray();
        assertNotNull(chars, "The character array representation should not be null.");
        assertEquals(0, chars.length, "The character array should be empty, confirming the builder starts in a pristine state.");
    }
}

package org.apache.commons.text.similarity;

import org.junit.jupiter.api.Test;

import java.util.HashMap;
import java.util.Map;

import static org.junit.jupiter.api.Assertions.assertThrows;

/**
 * Validates that the text similarity measurement system properly rejects
 * attempts to compare sequences when critical input information is missing.
 */
public class SimilarityInputValidationTest {

    @Test
    void testRejectsComparisonWhenInputIsMissing() {
        // Use LevenshteinDistance for raw text comparison without distance limitations.
        final LevenshteinDistance distance = new LevenshteinDistance();

        // Scenario 1: Left comparison text is missing.
        assertThrows(IllegalArgumentException.class, () -> {
            distance.apply(null, "right");
        });

        // Scenario 2: Right comparison text is missing.
        assertThrows(IllegalArgumentException.class, () -> {
            distance.apply("left", null);
        });

        // Use CosineSimilarity for structured input (input wrapper) comparison.
        final CosineSimilarity similarity = new CosineSimilarity();
        final Map<CharSequence, Integer> rightVector = new HashMap<>();
        rightVector.put("content", 1);
        final Map<CharSequence, Integer> leftVector = new HashMap<>();
        leftVector.put("content", 1);


        // Scenario 3: Left input wrapper is missing.
        assertThrows(IllegalArgumentException.class, () -> {
            similarity.apply(null, rightVector);
        });

        // Scenario 4: Right input wrapper is missing.
        assertThrows(IllegalArgumentException.class, () -> {
            similarity.apply(leftVector, null);
        });
    }
}

