package org.apache.commons.text.similarity;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.Assertions;

/**
 * Tests for rejecting null input in Damerau-Levenshtein Distance.
 */
public class DamerauLevenshteinDistanceNullInputTest {

    private DamerauLevenshteinDistance distance;

    @BeforeEach
    public void setUp() {
        distance = new DamerauLevenshteinDistance();
    }

    @Test
    public void testNullInputRejection() {
        // Test with raw strings
        Assertions.assertThrows(IllegalArgumentException.class, () -> {
            distance.apply(null, "right");
        });

        Assertions.assertThrows(IllegalArgumentException.class, () -> {
            distance.apply("left", null);
        });

        // Test with SimilarityInput objects
        SimilarityInput<Character> validInput = SimilarityInput.input("valid");

        Assertions.assertThrows(IllegalArgumentException.class, () -> {
            distance.apply(null, validInput);
        });

        Assertions.assertThrows(IllegalArgumentException.class, () -> {
            distance.apply(validInput, null);
        });
    }
}

