package org.apache.commons.text.similarity;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertThrows;

/**
 * Tests that the text similarity calculation system, when configured with a
 * maximum distance limit, properly rejects invalid comparison requests.
 */
public class BoundedSimilarityValidationTest {

    @Test
    void testRejectsIncompleteComparisonRequestsWithBoundedDistance() {
        // Establish a similarity calculator configured to operate within a bounded calculation range.
        final LevenshteinDistance distanceCalculator = new LevenshteinDistance(10);

        // Scenario 1: Attempt to calculate similarity when the initial text is missing.
        assertThrows(IllegalArgumentException.class, () -> distanceCalculator.apply(null, "valid text"),
                "Should throw when the first argument is null.");

        // Scenario 2: Attempt to calculate similarity when the comparison text is missing.
        assertThrows(IllegalArgumentException.class, () -> distanceCalculator.apply("valid text", null),
                "Should throw when the second argument is null.");

        // Scenario 3: Using structured input, test with the first input being absent.
        final EditDistanceFrom<Integer> structuredCalculatorWithFirstInputMissing = new EditDistanceFrom<>(distanceCalculator, null);
        assertThrows(IllegalArgumentException.class, () -> structuredCalculatorWithFirstInputMissing.apply("valid text"),
                "Structured calculation should fail when the first input is null.");

        // Scenario 4: Using structured input, test with the second input being absent.
        final EditDistanceFrom<Integer> structuredCalculatorWithSecondInputMissing = new EditDistanceFrom<>(distanceCalculator, "valid text");
        assertThrows(IllegalArgumentException.class, () -> structuredCalculatorWithSecondInputMissing.apply(null),
                "Structured calculation should fail when the second input is null.");
    }
}

