import java.nio.file.Paths;

import org.junit.jupiter.api.Test;
import org.apache.commons.text.StringSubstitutor;

/**
 * Validates that the default {@link StringSubstitutor} interpolation rejects attempts to process
 * variable expressions that reference external XML entities, preventing security vulnerabilities.
 */
public class StringSubstitutorExternalEntityTest {

    /**
     * Tests that a variable referencing an XML file with an external DTD reference is rejected.
     * The system should throw an {@link IllegalArgumentException} to prevent processing
     * potentially malicious external content.
     *
     * @throws URISyntaxException if the resource URI is invalid.
     */
    @Test
    public void testSubstitutionWithExternalEntityThrowsException() throws URISyntaxException {
        // Use the default interpolator which includes the XML string lookup.
        final StringSubstitutor interpolator = StringSubstitutor.createInterpolator();

        // Path to a test XML file that contains an external entity reference.
        // This file is designed to test for XXE vulnerabilities.
        final URL resourceUrl = getClass().getResource("/org/apache/commons/text/document-external-dtd.xml");
        final Path resourcePath = Paths.get(resourceUrl.toURI());

        // The input string contains a variable that directs the xml-lookup to parse the malicious file.
        // We don't need a specific XPath, just referencing the file should trigger the secure parser.
        final String input = "${xml:" + resourcePath.toString() + "}";

        // Verify that the operation fails with an IllegalArgumentException,
        // confirming that the external entity was detected and blocked.
        assertThrows(IllegalArgumentException.class, () -> {
            interpolator.replace(input);
        });
    }
}

