package org.apache.commons.text;

import org.junit.jupiter.api.Test;
import static org.assertj.core.api.Assertions.assertThat;
import static org.assertj.core.api.Assertions.assertThatThrownBy;

/**
 * Tests for {@link TextStringBuilder} insertion and boundary protection.
 */
public class TextStringBuilderInsertTest {

    @Test
    public void testInsertAtEndAndBoundaryChecks() {
        // Begin with an empty text composition instance and confirm its initial empty state
        final TextStringBuilder builder = new TextStringBuilder();
        assertThat(builder.toString()).isEmpty();
        assertThat(builder.isEmpty()).isTrue();

        // Insert a text segment at the start position and verify
        builder.insert(0, "start");
        assertThat(builder.toString()).isEqualTo("start");

        // Attempt to insert content at positions before the valid range and beyond the current end boundary
        assertThatThrownBy(() -> builder.insert(-1, "invalid"))
            .isInstanceOf(StringIndexOutOfBoundsException.class);
        assertThatThrownBy(() -> builder.insert(builder.length() + 1, "invalid"))
            .isInstanceOf(StringIndexOutOfBoundsException.class);

        // Insert a boolean value at the current end position and confirm
        builder.insert(builder.length(), true);
        assertThat(builder.toString()).isEqualTo("starttrue");

        // Verify boundary enforcement remains intact
        assertThatThrownBy(() -> builder.insert(-1, false))
            .isInstanceOf(StringIndexOutOfBoundsException.class);
        assertThatThrownBy(() -> builder.insert(builder.length() + 1, false))
            .isInstanceOf(StringIndexOutOfBoundsException.class);

        // Insert a single character at the end and validate
        builder.insert(builder.length(), '!');
        assertThat(builder.toString()).isEqualTo("starttrue!");

        // Insert a sequence of characters at the end position and confirm
        final char[] charArray = {'a', 'b', 'c'};
        builder.insert(builder.length(), charArray);
        assertThat(builder.toString()).isEqualTo("starttrue!abc");

        // Verify that attempts to insert at positions beyond the new boundary are properly rejected
        assertThatThrownBy(() -> builder.insert(builder.length() + 1, charArray))
            .isInstanceOf(StringIndexOutOfBoundsException.class);

        // Insert a portion of a character sequence by specifying a subset range
        final char[] partialCharArray = {'x', 'y', 'z'};
        builder.insert(builder.length(), partialCharArray, 1, 2); // Inserts 'yz'
        assertThat(builder.toString()).isEqualTo("starttrue!abcyz");

        // Ensure invalid positions continue to be blocked
        assertThatThrownBy(() -> builder.insert(builder.length() + 1, partialCharArray, 0, 1))
            .isInstanceOf(StringIndexOutOfBoundsException.class);

        // Insert numeric values of various types
        builder.insert(builder.length(), 123.45d);
        assertThat(builder.toString()).isEqualTo("starttrue!abcyz123.45");
        assertThatThrownBy(() -> builder.insert(builder.length() + 1, 0.0d))
            .isInstanceOf(StringIndexOutOfBoundsException.class);

        builder.insert(builder.length(), 67.8f);
        assertThat(builder.toString()).isEqualTo("starttrue!abcyz123.4567.8");
        assertThatThrownBy(() -> builder.insert(builder.length() + 1, 0.0f))
            .isInstanceOf(StringIndexOutOfBoundsException.class);

        builder.insert(builder.length(), 90);
        assertThat(builder.toString()).isEqualTo("starttrue!abcyz123.4567.890");
        assertThatThrownBy(() -> builder.insert(builder.length() + 1, 0))
            .isInstanceOf(StringIndexOutOfBoundsException.class);

        builder.insert(builder.length(), 12345678901L);
        assertThat(builder.toString()).isEqualTo("starttrue!abcyz123.4567.89012345678901");
        assertThatThrownBy(() -> builder.insert(builder.length() + 1, 0L))
            .isInstanceOf(StringIndexOutOfBoundsException.class);

        // Insert an object reference at the end position and confirm
        final Object obj = new Object() {
            @Override
            public String toString() {
                return "[OBJ]";
            }
        };
        builder.insert(builder.length(), obj);
        assertThat(builder.toString()).isEqualTo("starttrue!abcyz123.4567.89012345678901[OBJ]");

        // Final boundary check
        assertThatThrownBy(() -> builder.insert(-1, obj))
            .isInstanceOf(StringIndexOutOfBoundsException.class);
        assertThatThrownBy(() -> builder.insert(builder.length() + 1, obj))
            .isInstanceOf(StringIndexOutOfBoundsException.class);
    }
}

package org.apache.commons.text;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertThrows;

/**
 * Tests the insert methods of the TextStringBuilder class.
 */
public class TextStringBuilderInsertTest {

    @Test
    public void testInsert() {
        // Initial state
        final TextStringBuilder sb = new TextStringBuilder();
        assertEquals("", sb.toString());

        // Insert String
        sb.insert(0, "test");
        assertEquals("test", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, "invalid"));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(5, "invalid"));

        // Insert Boolean
        sb.insert(sb.length(), true);
        assertEquals("testtrue", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, false));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(9, true));

        // Insert Char
        sb.insert(sb.length(), '!');
        assertEquals("testtrue!", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, '!'));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(10, '!'));

        // Insert Char Array
        final char[] charArray = {'a', 'b', 'c'};
        sb.insert(sb.length(), charArray);
        assertEquals("testtrue!abc", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, charArray));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(14, charArray));

        // Insert Char Array subset
        final char[] partialArray = {'x', 'y', 'z'};
        sb.insert(sb.length(), partialArray, 1, 1);
        assertEquals("testtrue!abcy", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, partialArray, 0, 1));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(15, partialArray, 0, 1));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(14, partialArray, -1, 1));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(14, partialArray, 0, 4));

        // Insert Double
        sb.insert(sb.length(), 1.23);
        assertEquals("testtrue!abcy1.23", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, 1.23));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(19, 1.23));

        // Insert Float
        sb.insert(sb.length(), 4.5f);
        assertEquals("testtrue!abcy1.234.5", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, 4.5f));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(22, 4.5f));

        // Insert Int
        sb.insert(sb.length(), 67);
        assertEquals("testtrue!abcy1.234.567", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, 67));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(24, 67));

        // Insert Long
        sb.insert(sb.length(), 890L);
        assertEquals("testtrue!abcy1.234.567890", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, 890L));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(28, 890L));

        // Insert Object
        final Object obj = new Object() {
            @Override
            public String toString() {
                return "obj";
            }
        };
        sb.insert(sb.length(), obj);
        assertEquals("testtrue!abcy1.234.567890obj", sb.toString());
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(-1, obj));
        assertThrows(IndexOutOfBoundsException.class, () -> sb.insert(30, obj));
    }
}

