package org.apache.commons.text.similarity;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * Tests to validate that the edit distance calculation is symmetric.
 */
public class LevenshteinDistanceSymmetryTest {

    /**
     * This test verifies that the Levenshtein distance between two sequences is the same regardless of the order of comparison.
     */
    @Test
    public void testEditDistanceSymmetry() {
        // 1. Prepare two distinct text samples and their expected similarity score.
        final CharSequence sequenceA = "Apache Commons Text";
        final CharSequence sequenceB = "Apache Commons";
        final Integer expectedDistance = 5; // The distance is the 5 characters in " Text"

        // Instantiate the LevenshteinDistance class
        final LevenshteinDistance distanceCalculator = new LevenshteinDistance();

        // 2. Submit both sequences to the similarity calculation service in forward order.
        final Integer forwardDistance = distanceCalculator.apply(sequenceA, sequenceB);

        // 3. Submit both sequences to the similarity calculation service in reverse order.
        final Integer reverseDistance = distanceCalculator.apply(sequenceB, sequenceA);

        // 4. Confirm that each comparison produces the same expected distance value.
        assertEquals(expectedDistance, forwardDistance, "The forward distance calculation should match the expected value.");
        assertEquals(expectedDistance, reverseDistance, "The reverse distance calculation should match the expected value.");

        // 5. Verify the calculation remains symmetric.
        assertEquals(forwardDistance, reverseDistance, "The distance calculation should be symmetric, yielding the same result regardless of order.");
    }
}

