        defaultInstance = new DamerauLevenshteinDistance();
    }

    static Stream<Arguments> limitedDamerauLevenshteinDistanceTestCases() {
        return Stream.of(
                of("", "", 0, 0),
                of("abc", "abc", 1, 0),
                of("abc", "adc", 1, 1),
                of("abc", "acb", 1, 1),
                of("abc", "def", 1, -1),
                of("abc", "ab", 2, 1),
                of("ca", "abc", 3, 3),
                of("a", "b", 0, -1),
                of("a", "a", 0, 0),
