    @MethodSource("limitedDamerauLevenshteinDistanceTestCases")
    void testLimitedCompare(String left, String right, int threshold, int expectedDistance) {
        final DamerauLevenshteinDistance instance = new DamerauLevenshteinDistance(threshold);
        final Integer leftRightDistance = instance.apply(left, right);
        final Integer rightLeftDistance = instance.apply(right, left);
