package org.apache.commons.text.similarity;

import static org.junit.jupiter.api.Assertions.assertEquals;

import org.junit.jupiter.api.Test;

/**
 * Unit tests for {@link LevenshteinDistance} with a threshold.
 */
public class LevenshteinDistanceThresholdTest {

    @Test
    public void testLevenshteinDistanceWithThreshold() {
        // Scenario 1: Distance is below threshold
        LevenshteinDistance distance1 = new LevenshteinDistance(2);
        String left1 = "frog";
        String right1 = "fog";
        int expected1 = 1;
        assertEquals(expected1, distance1.apply(left1, right1));
        assertEquals(expected1, distance1.apply(right1, left1));

        // Scenario 2: Distance is above threshold, should return -1
        LevenshteinDistance distance2 = new LevenshteinDistance(3);
        String left2 = "elephant";
        String right2 = "hippo";
        int expected2 = -1;
        assertEquals(expected2, distance2.apply(left2, right2));
        assertEquals(expected2, distance2.apply(right2, left2));

        // Scenario 3: Distance is equal to threshold
        LevenshteinDistance distance3 = new LevenshteinDistance(3);
        String left3 = "fly";
        String right3 = "ant";
        int expected3 = 3;
        assertEquals(expected3, distance3.apply(left3, right3));
        assertEquals(expected3, distance3.apply(right3, left3));

        // Scenario 4: One string is empty
        LevenshteinDistance distance4 = new LevenshteinDistance(1);
        String left4 = "";
        String right4 = "a";
        int expected4 = 1;
        assertEquals(expected4, distance4.apply(left4, right4));
        assertEquals(expected4, distance4.apply(right4, left4));

        // Scenario 5: Strings are identical
        LevenshteinDistance distance5 = new LevenshteinDistance(1);
        String left5 = "apple";
        String right5 = "apple";
        int expected5 = 0;
        assertEquals(expected5, distance5.apply(left5, right5));
        assertEquals(expected5, distance5.apply(right5, left5));

        // Scenario 6: Distance is above threshold of 0
        LevenshteinDistance distance6 = new LevenshteinDistance(0);
        String left6 = "a";
        String right6 = "b";
        int expected6 = -1;
        assertEquals(expected6, distance6.apply(left6, right6));
        assertEquals(expected6, distance6.apply(right6, left6));

        // Scenario 7: Zero length strings
        LevenshteinDistance distance7 = new LevenshteinDistance(0);
        assertEquals(0, distance7.apply("", ""));
    }
}

