package org.apache.commons.text.similarity;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * Tests {@link LevenshteinDistance}.
 */
public class LevenshteinDistanceTest1 {

    @Test
    public void testDistanceCalculationScenarios() {
        // GIVEN: a text similarity calculator configured to operate without any distance limitations.
        final LevenshteinDistance distanceCalculator = new LevenshteinDistance();

        // WHEN/THEN: Validate with identical sequences that require no modifications.
        assertEquals(0, distanceCalculator.apply("", ""));
        assertEquals(0, distanceCalculator.apply("abc", "abc"));

        // WHEN/THEN: Validate with sequences differing by single character changes.
        final int singleChangeDistance = distanceCalculator.apply("abc", "abd");
        assertEquals(1, singleChangeDistance);
        assertEquals(singleChangeDistance, distanceCalculator.apply("abd", "abc"), "Distance calculation should be symmetrical.");

        // WHEN/THEN: Validate with sequences requiring multiple edits.
        final int multipleEditsDistance = distanceCalculator.apply("saturday", "sunday");
        assertEquals(3, multipleEditsDistance);
        assertEquals(multipleEditsDistance, distanceCalculator.apply("sunday", "saturday"), "Distance calculation should be symmetrical.");

        // WHEN/THEN: Validate with completely different sequences.
        final int differentSequencesDistance = distanceCalculator.apply("cat", "dog");
        assertEquals(3, differentSequencesDistance);
        assertEquals(differentSequencesDistance, distanceCalculator.apply("dog", "cat"), "Distance calculation should be symmetrical.");

        // WHEN/THEN: Validate with sequences involving character transpositions.
        final int transpositionDistance = distanceCalculator.apply("ab", "ba");
        assertEquals(2, transpositionDistance, "Levenshtein distance treats transposition as two modifications.");
        assertEquals(transpositionDistance, distanceCalculator.apply("ba", "ab"), "Distance calculation should be symmetrical.");

        // WHEN/THEN: Validate symmetry for deletion and insertion.
        final int deletionDistance = distanceCalculator.apply("book", "bok");
        assertEquals(1, deletionDistance);
        final int insertionDistance = distanceCalculator.apply("bok", "book");
        assertEquals(1, insertionDistance);
        assertEquals(deletionDistance, insertionDistance, "Symmetrical comparison of deletion and insertion should yield the same distance.");
    }
}

