 */
public class DamerauLevenshteinDistanceSymmetryTest {

    private static DamerauLevenshteinDistance distance;

    @BeforeAll
    static void setUp() {
        distance = new DamerauLevenshteinDistance();
    }

    private static Stream<Arguments> symmetryProvider() {
        return Stream.of(
            Arguments.of("abc", "abc", 0, SimilarityCharacterInput.class),
            Arguments.of("abc", "def", 3, SimilarityCharacterInput.class),
            Arguments.of("", "", 0, SimilarityCharacterInput.class),
            Arguments.of("a", "", 1, SimilarityCharacterInput.class),
            Arguments.of("", "a", 1, SimilarityCharacterInput.class),
            // Test transposition
            Arguments.of("abc", "acb", 1, SimilarityCharacterInput.class),
            // Test complex cases
            Arguments.of("ca", "abc", 3, SimilarityCharacterInput.class),
            Arguments.of("elephant", "hippo", 7, SimilarityCharacterInput.class),
            Arguments.of("this is a test", "this is a new test", 3, SimilarityCharacterInput.class)
        );
    }

    private SimilarityInput<CharSequence> createSimilarityInput(
        final String text,
        final Class<? extends SimilarityInput<CharSequence>> type) throws Exception {
        final Constructor<? extends SimilarityInput<CharSequence>> ctor = type.getConstructor(CharSequence.class);
        return ctor.newInstance(text);
    }

    @ParameterizedTest
    @MethodSource("symmetryProvider")
    void testSymmetry(
        final String left,
        final String right,
        final int expected,
        final Class<? extends SimilarityInput<CharSequence>> type) throws Exception {

        final SimilarityInput<CharSequence> leftInput = createSimilarityInput(left, type);
        final SimilarityInput<CharSequence> rightInput = createSimilarityInput(right, type);

        // Compute distance in both directions
        final int forwardDistance = distance.apply(leftInput, rightInput);
        final int backwardDistance = distance.apply(rightInput, leftInput);

        // Assert that both calculations match the expected value, verifying symmetry
        assertEquals(expected, forwardDistance, "Forward distance calculation is incorrect.");
        assertEquals(expected, backwardDistance, "Backward distance calculation is incorrect, violating symmetry.");
