/**
 * Tests {@link OctalUnescaper} to validate that it correctly processes octal escape
 * sequences while preserving adjacent special character encodings.
 */
public class OctalUnescaperTest1 {

    /**
     * Tests the scenario where octal escape sequences are mixed with various Unicode
     * character representations, ensuring only the octal values are converted.
     */
    @Test
    public void testOctalUnescapingWithAdjacentUnicodeEncodings() {
        // Configure the transformation system to handle only octal notation conversions.
        final CharSequenceTranslator octalUnescaper = new OctalUnescaper();

        // Scenario 1: An octal sequence followed by a high surrogate marker.
        // The unescaper should convert the octal '\377' (U+00FF) and preserve the '\uD83D' text.
        final String inputWithHighSurrogate = "\377\uD83D";
        final String expectedWithHighSurrogate = "ÿ\uD83D";
        assertEquals(expectedWithHighSurrogate, octalUnescaper.translate(inputWithHighSurrogate));

        // Scenario 2: An octal sequence mixed with a complete rocket emoji representation.
        // It should convert '\123' ('S') and preserve the emoji's Unicode encoding.
        final String inputWithRocketEmoji = "\123\uD83D\uDE80";
        final String expectedWithRocketEmoji = "S\uD83D\uDE80";
        assertEquals(expectedWithRocketEmoji, octalUnescaper.translate(inputWithRocketEmoji));

        // Scenario 3: An octal sequence preceding a different complete emoji.
        // This validates the behavior with another emoji encoding.
        final String inputWithFaceEmoji = "\123\uD83D\uDE00";
        final String expectedWithFaceEmoji = "S\uD83D\uDE00";
        assertEquals(expectedWithFaceEmoji, octalUnescaper.translate(inputWithFaceEmoji));

        // Scenario 4: An edge case where content ends with an octal sequence followed by an isolated high surrogate.
        // This verifies correct handling at the end of a string.
        final String inputEndingWithSurrogate = "\45\uD83D";
        final String expectedEndingWithSurrogate = "%\uD83D"; // Octal 45 is '%'
        assertEquals(expectedEndingWithSurrogate, octalUnescaper.translate(inputEndingWithSurrogate));
    }
}

