package org.apache.commons.text.similarity;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;

import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * Tests for {@link LevenshteinDistance}, focusing on symmetry and threshold behavior.
 */
public class LevenshteinDistanceThresholdSymmetryTest {

    @ParameterizedTest
    @CsvSource({
        "foo, foo, 10, 0",
        "foo, bar, 10, 3",
        "foo, bar, 3, 3",
        "foo, bar, 2, -1",
        "frog, fog, 1, 1",
        "fly, fly, 1, 0",
        "a, b, 1, 1",
        "a, b, 0, -1",
        "apache, a fan, 5, 5",
        "apache, a fan, 4, -1",
        "elephant, relevant, 3, 3",
        "elephant, relevant, 2, -1",
        "same, same, 0, 0",
        "'', a, 1, 1",
        "a, '', 1, 1",
        "'', '', 0, 0"
    })
    void testSymmetryAndThreshold(final String left, final String right, final int threshold, final int expected) {
        final LevenshteinDistance distance = new LevenshteinDistance(threshold);

        final int distance1 = distance.apply(left, right);
        final int distance2 = distance.apply(right, left);

        assertEquals(expected, distance1, "Distance from left to right should match expected value.");
        assertEquals(distance1, distance2, "Distance calculation should be symmetrical.");
    }
}

