package org.apache.commons.text.translate;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

/**
 * Unit tests for {@link OctalUnescaper}.
 */
public class OctalUnescaperTest1 {

    @Test
    public void testSurrogatePairsAndEmoji() {
        final OctalUnescaper octalUnescaper = new OctalUnescaper();

        // Scenario 1: Octal escape sequence followed by a high surrogate character and trailing text
        final String input1 = "\\134\\uD800abc";
        final String expected1 = "\\\\\\uD800abc";
        final String result1 = octalUnescaper.translate(input1);
        assertEquals(expected1, result1, "Octal unescaping failed to preserve high surrogate.");

        // Scenario 2: Octal escape sequence followed by a complete emoji and additional text
        final String input2 = "\\134\uD83D\uDE03abc";
        final String expected2 = "\\\\\uD83D\uDE03abc";
        final String result2 = octalUnescaper.translate(input2);
        assertEquals(expected2, result2, "Octal unescaping failed to preserve emoji surrogate pair.");

        // Scenario 3: Octal escape sequence followed by a different emoji and text
        final String input3 = "\\134\uD83D\uDE0Eabc";
        final String expected3 = "\\\\\uD83D\uDE0Eabc";
        final String result3 = octalUnescaper.translate(input3);
        assertEquals(expected3, result3, "Octal unescaping failed to preserve a different emoji surrogate pair.");

        // Scenario 4: Octal escape sequence and a lone high surrogate
        final String input4 = "\\134\\uD800";
        final String expected4 = "\\\\\\uD800";
        final String result4 = octalUnescaper.translate(input4);
        assertEquals(expected4, result4, "Octal unescaping failed to handle incomplete surrogate pairs correctly.");
    }
}

