 */
public class StringSubstitutorCircularReferenceTest {

    /**
     * Tests that a circular reference in a variable definition is detected and
     * causes an IllegalStateException.
     */
    @Test
    public void testCircularReferenceThrowsIllegalStateException() {
        final Map<String, String> map = new HashMap<>();
        map.put("name", "Hello <name>");
        final StringLookup mapLookup = StringLookupFactory.INSTANCE.mapStringLookup(map);
        final StringSubstitutor substitutor = new StringSubstitutor(mapLookup, "<", ">", '`');

        final IllegalStateException ex = assertThrows(IllegalStateException.class, () -> {
            substitutor.replace("Hi <name>!");
        }, "Expected an IllegalStateException to be thrown for a circular reference");

        final String expectedMessage = "Infinite loop in property interpolation of 'Hi <name>!';";
        assertEquals(expectedMessage, ex.getMessage().substring(0, expectedMessage.length()));
    }
}

