@TestMethodOrder(MethodOrderer.MethodName.class)
class StringSubstitutorWithDefaultsTest {

    protected Map<String, String> values;

    private static final String ACTUAL_ANIMAL = "fox";
    private static final String ACTUAL_TARGET = "dog";
    private static final String CLASSIC_RESULT = "The quick brown fox jumps over the lazy dog.";
    private static final String CLASSIC_TEMPLATE = "The quick brown ${animal} jumps over the lazy ${target}.";
    private static final String EMPTY_EXPR = "${}";

