package org.apache.commons.text;

import static org.junit.jupiter.api.Assertions.assertFalse;
import static org.junit.jupiter.api.Assertions.assertTrue;

import org.junit.jupiter.api.Test;

/**
 * Create a test case to validate the system's ability to correctly identify
 * characters as word separators under various configurations.
 */
public class WordSeparatorIdentificationTest {

    @Test
    public void testIsWordSeparator() {
        // 1. Test default behavior: No custom separators provided.
        // A space should be a separator, but a period should not. We verify this by
        // checking the output of capitalizeFully.
        assertTrue(WordUtils.capitalizeFully("a b").equals("A B"), "A space must be a default word separator.");
        assertFalse(WordUtils.capitalizeFully("a.b").equals("A.B"), "A period must not be a default word separator.");

        // 2. Test custom separators that "exclude" the default space.
        // When a custom separator set is provided, whitespace is no longer a separator.
        final char[] customSeparators1 = { '_' };
        assertFalse(WordUtils.capitalizeFully("a b", customSeparators1).equals("A B"),
                "A space must not be a separator when a custom set without it is provided.");

        // 3. Test custom separators: including a character makes it a separator.
        // Here, the period is explicitly included.
        final char[] customSeparators2 = { '.' };
        assertTrue(WordUtils.capitalizeFully("a.b", customSeparators2).equals("A.B"),
                "A period must be a separator when explicitly included in the custom set.");

        // 4. Test with multiple custom separators.
        final char[] multipleSeparators = { '.', '_', 'z' };
        // Space should not be a separator as it is not in the custom set.
        assertFalse(WordUtils.capitalizeFully("a b", multipleSeparators).equals("A B"),
                "A space must not be a separator if not in the multi-character custom set.");
        // Period should be a separator as it IS in the custom set.
        assertTrue(WordUtils.capitalizeFully("a.b", multipleSeparators).equals("A.B"),
                "A period must be a separator when part of a multi-character custom set.");

        // 5. Test with duplicate entries in the custom separator list.
        final char[] separatorsWithDuplicates = { '.', '_', 'z', '.' };
        // The presence of duplicates should not affect the behavior.
        assertTrue(WordUtils.capitalizeFully("a.b", separatorsWithDuplicates).equals("A.B"),
                "A period must be a separator even when duplicated in the custom set.");
    }
}

