import static org.junit.jupiter.api.Assertions.assertTrue;

/**
 * Tests for validating word separator character identification.
 */
public class WordSeparatorValidationTest {

    @Test
    public void testIsWordSeparator() {
        // When no custom separators are defined, default to whitespace.
        final StrMatcher defaultMatcher = StrMatcher.spaceMatcher();
        assertFalse(defaultMatcher.isMatch(new char[]{'.'}, 0) > 0,
            "A period should not be a separator by default.");
        assertTrue(defaultMatcher.isMatch(new char[]{' ''}, 0) > 0,
            "A space should be a separator by default.");

        // When a custom separator set with a period is provided.
        final StrMatcher customMatcher = StrMatcher.charSetMatcher('.');
        assertFalse(customMatcher.isMatch(new char[]{' ''}, 0) > 0,
            "A space should not be a separator when only '.' is a custom separator.");
        assertTrue(customMatcher.isMatch(new char[]{'.'}, 0) > 0,
            "A period should be a separator when it is the only custom separator.");

        // When multiple custom separators are provided.
        final StrMatcher multiMatcher = StrMatcher.charSetMatcher('.', '_', 'a');
        assertFalse(multiMatcher.isMatch(new char[]{' ''}, 0) > 0,
            "A space should not be a separator when the custom set is '.', '_', 'a'.");

        // When multiple custom separators are provided, including duplicates.
        final StrMatcher multiWithDupesMatcher = StrMatcher.charSetMatcher('.', '_', 'a', '.');
        assertTrue(multiWithDupesMatcher.isMatch(new char[]{'.'}, 0) > 0,
            "A period should be identified as a separator when in a set with duplicates.");
    }
}

