import org.apache.commons.text.CharacterPredicate;
import org.apache.commons.text.RandomStringGenerator;
import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;

/**
 * Tests for {@link RandomStringGenerator} focusing on filter replacement behavior.
 */
public class RandomStringGeneratorFilteringTest {

    @Test
    void testReplacingFilterHonorsLastSuppliedCriterion() {
        // Define an initial filter that only accepts 'a'.
        final CharacterPredicate initialCriterion = codePoint -> codePoint == 'a';

        // Define a second filter that only accepts 'b'.
        final CharacterPredicate finalCriterion = codePoint -> codePoint == 'b';

        // Build the generator, applying the first rule and then replacing it with the second.
        final RandomStringGenerator generator = new RandomStringGenerator.Builder()
            .withinRange('a', 'z')
            .filteredBy(initialCriterion)  // This rule should be disregarded.
            .filteredBy(finalCriterion)    // This rule should be honored.
            .build();

        // Generate a string of 100 characters.
        final String generatedString = generator.generate(100);

        // Verify that the generated string has the correct length.
        assertThat(generatedString).hasSize(100);

        // Examine each character to ensure it matches 'b', proving the final criterion was used.
        for (final char c : generatedString.toCharArray()) {
            assertThat(c).as("Character in generated string").isEqualTo('b');
        }
    }
}

