import static org.junit.jupiter.api.Assertions.assertTrue;

/**
 * Tests for the accumulation behavior of {@link RandomStringGenerator.Builder}.
 */
public class RandomStringGeneratorAccumulationTest {

    @ParameterizedTest
    @ValueSource(booleans = {true, false})
    void testAccumulation(final boolean accumulate) {
        final String referenceString = "abcde";
        final RandomStringGenerator.Builder builder = new RandomStringGenerator.Builder()
                .setAccumulate(accumulate)
                .withinRange('a', 'e')
                .withinRange('a', 'd')
                .withinRange('a', 'c')
                .selectFrom((char[]) null) // Verify robustness with null argument
                .selectFrom() // Verify robustness with empty argument
                .withinRange('a', 'b');

        final RandomStringGenerator generator = builder.build();
        final String randomString = generator.generate(10);

        if (accumulate) {
            // When accumulating, all characters from all ranges should be available.
            for (final char c : randomString.toCharArray()) {
                assertTrue(referenceString.indexOf(c) >= 0,
                        () -> "With accumulation, character '" + c + "' should be in the reference set '"
                                + referenceString + "'.");
            }
        } else {
            // When not accumulating, only characters from the last range should be available.
            final String finalSet = "ab";
            for (final char c : randomString.toCharArray()) {
                assertTrue(finalSet.indexOf(c) >= 0,
                        () -> "Without accumulation, character '" + c + "' should be in the final set '"
                                + finalSet + "'.");
            }
        }
    }
}

