        // Build a RandomStringGenerator instance. The test description implies that chaining
        // 'withinRange' and 'selectFrom' calls enables an "accumulation mode" where all
        // specified character sources are combined.
        final RandomStringGenerator generator = new RandomStringGenerator.Builder()
                .withinRange('a', 'z')
                .withinRange('A', 'Z')
                .withinRange('0', '9')
                .selectFrom(punctuation)
                .build();

