        for (final char c : randomString.toCharArray()) {
            final boolean isLetter = Character.isLetter(c);
            final boolean isDigit = Character.isDigit(c);
            // Use binary search on the sorted punctuation array for efficient checking.
            final boolean isPunctuation = Arrays.binarySearch(punctuation, c) >= 0;

            assertTrue(isLetter || isDigit || isPunctuation,
