package org.apache.commons.text;

import static org.junit.jupiter.api.Assertions.assertThrows;
import static org.junit.jupiter.api.Assertions.assertTrue;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;

import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.io.TempDir;

/**
 * Tests that the default {@link StringSubstitutor} interpolation settings prevent access to external document content.
 */
class StringSubstitutorSecurityTest {

    @TempDir
    private Path tempDir;

    @Test
    void testXmlLookupSecurityEnforcedByDefault() throws IOException {
        // Setup: Create a temporary XML file that could be maliciously accessed.
        final Path xmlFile = tempDir.resolve("document.xml");
        Files.writeString(xmlFile, "<root><user>admin</user></root>");

        // Action: Use the default interpolator, which should have security controls active.
        final StringSubstitutor substitutor = StringSubstitutor.createInterpolator();
        final String vulnerableInput = "${xml:" + xmlFile.toAbsolutePath() + ":/root/user}";

        // Verification: Assert that the substitution fails with an exception,
        // indicating that the lookup is blocked by default.
        final IllegalArgumentException exception = assertThrows(IllegalArgumentException.class, () -> {
            substitutor.replace(vulnerableInput);
        });

        // The exact message can vary, but it must indicate that the variable resolution failed.
        // This confirms that the 'xml' lookup, which can access files, is not enabled in the default configuration.
        final String message = exception.getMessage();
        assertTrue(message.contains("Variable 'xml:") && message.contains("' is not recognized"),
                "Expected exception message to indicate variable resolution failure for security reasons.");
    }
}

