    @Test
    public void testInterpolatorDoesNotResolveExternalEntities() {
        // The file 'document-external-dtd.xml' is expected to reference an external DTD.
        // The 'xml' lookup should be configured to disallow this for security reasons.
        final StringSubstitutor interpolator = StringSubstitutor.createInterpolator();

        // The XPath here may or may not exist. The key is that parsing the XML
        // document itself should fail due to the external entity, before the XPath is even evaluated.
        final String lookupString = "${xml:org/apache/commons/text/document-external-dtd.xml:/root/path}";

        // Expect an IllegalArgumentException, indicating the operation was blocked.
        assertThrows(IllegalArgumentException.class, () -> {
            interpolator.replace(lookupString);
        });
    }
}

